CREATE OR REPLACE PROCEDURE pls_insere_fornec_material_aud ( nr_seq_item_p bigint, cd_fornecedor_p bigint, vl_preco_p bigint, nr_seq_material_forn_p bigint, nm_usuario_p text, ie_origem_item_p text) AS $body$
BEGIN

if (coalesce(cd_fornecedor_p, 0) <> 0) then
	update	pls_auditoria_item
	set	nr_seq_prest_fornec 	= pls_obter_prestador_fornecedor(cd_fornecedor_p),
		nr_seq_material_forn	 = NULL,
		vl_item			= vl_preco_p
	where	nr_sequencia 		= nr_seq_item_p;

	if (ie_origem_item_p = 'A') then
	CALL pls_gerar_hist_log_req(	nr_seq_item_p,4,nm_usuario_p,0,0,0,0);
	end if;
elsif (coalesce(nr_seq_material_forn_p, 0) <> 0) then
	update	pls_auditoria_item
	set	nr_seq_material_forn	= nr_seq_material_forn_p,
		nr_seq_prest_fornec	 = NULL,
		vl_item			= vl_preco_p
	where	nr_sequencia 		= nr_seq_item_p;
	if (ie_origem_item_p = 'A') then
	CALL pls_gerar_hist_log_req(	nr_seq_item_p,4,nm_usuario_p,0,0,0,0);
	end if;
end if;
commit;

end;
$body$
