CREATE OR REPLACE PROCEDURE pls_insere_grupo_auditor_fax ( nr_seq_grupo_p bigint, nr_seq_auditoria_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ordem_w			bigint;
nr_seq_grupo_w			bigint;
qt_grupo_ordem_w		integer;


BEGIN
begin
	select	nr_seq_ordem
	into STRICT	nr_seq_ordem_w
	from	pls_auditoria_grupo
	where	nr_sequencia = coalesce(pls_obter_grupo_analise_atual(nr_seq_auditoria_p),0);

exception
when others then
	nr_seq_ordem_w := 1;
end;

select	nextval('pls_auditoria_grupo_seq')
into STRICT	nr_seq_grupo_w
;

insert into pls_auditoria_grupo(nr_sequencia, nr_seq_auditoria, nr_seq_grupo,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
	nm_usuario_nrec, nr_seq_ordem, ie_status,
	ie_manual)
values (nr_seq_grupo_w, nr_seq_auditoria_p, nr_seq_grupo_p,
	clock_timestamp(), nm_usuario_p, clock_timestamp(),
	nm_usuario_p,nr_seq_ordem_w ,'U',
	'S');

select	count(*)
into STRICT	qt_grupo_ordem_w
from	pls_auditoria_grupo
where	nr_seq_ordem		= nr_seq_ordem_w
and	nr_seq_auditoria	= nr_seq_auditoria_p;

if (qt_grupo_ordem_w > 1) then
	CALL pls_ordena_grupo_aud_fax(nr_seq_ordem_w,nr_seq_grupo_w,nr_seq_auditoria_p,nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
