CREATE OR REPLACE PROCEDURE pls_insere_paracer_guia_int ( nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_observacao_w			varchar(255);

C01 CURSOR FOR
	SELECT	substr(ds_observacao,1,255)
	from	pls_guia_plano_historico
	where	nr_seq_guia		= nr_seq_guia_p
	and	(ie_status_analise IS NOT NULL AND ie_status_analise::text <> '');

BEGIN

open C01;
loop
fetch C01 into
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

update	pls_guia_plano
set	ds_observacao	= ds_observacao_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_guia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
