CREATE OR REPLACE PROCEDURE pls_insere_senha_externa_estip ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, cd_senha_externa_p text, dt_validade_senha_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	update	pls_requisicao
	set	cd_senha_externa	= cd_senha_externa_p,
		dt_valid_senha_ext	= CASE WHEN dt_validade_senha_p = NULL THEN  dt_validade_senha  ELSE dt_validade_senha_p END ,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_requisicao_p;

	CALL pls_requisicao_gravar_hist(nr_seq_requisicao_p, 'L', substr('O usuário '||nm_usuario_p||' inseriu manualmente senha externa '||cd_senha_externa_p||' e data de validade '||dt_validade_senha_p||'.',1,4000), null, nm_usuario_p);

	elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	update	pls_guia_plano
	set	cd_senha_externa	= cd_senha_externa_p,
		dt_valid_senha_ext	= CASE WHEN dt_validade_senha_p = NULL THEN  dt_validade_senha  ELSE dt_validade_senha_p END ,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_guia_p;

	CALL pls_guia_gravar_historico(	nr_seq_guia_p, 2, substr('O usuário '||nm_usuario_p||' inseriu manualmente senha externa '||cd_senha_externa_p||' e data de validade '||dt_validade_senha_p||'.',1,4000), null, nm_usuario_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
