CREATE OR REPLACE PROCEDURE pls_inserir_carencia_padrao ( nr_seq_contrato_p bigint, nr_seq_segurado_p bigint, nr_seq_tipo_carencia_p bigint, qt_dia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_conta_w			bigint;
dt_inclusao_operadora_w		pls_segurado.dt_inclusao_operadora%type;
dt_fim_vigencia_w		timestamp;


BEGIN
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	select	count(*)
	into STRICT	qt_conta_w
	from 	pls_carencia
	where	nr_seq_segurado		= nr_seq_segurado_p
	and	nr_seq_tipo_carencia 	= nr_seq_tipo_carencia_p;

	select	dt_inclusao_operadora
	into STRICT	dt_inclusao_operadora_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p;

	dt_fim_vigencia_w := dt_inclusao_operadora_w + qt_dia_p;

	if (qt_conta_w	= 0) then
		insert into pls_carencia(nr_sequencia,nm_usuario,dt_atualizacao,
			nr_seq_segurado,nr_seq_tipo_carencia,qt_dias,ie_origem_carencia_benef, dt_inicio_vigencia, dt_fim_vigencia)
		values (nextval('pls_carencia_seq'),nm_usuario_p,clock_timestamp(),
			nr_seq_segurado_p,nr_seq_tipo_carencia_p,qt_dia_p,'P', dt_inclusao_operadora_w, dt_fim_vigencia_w);
	end if;

elsif (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	count(*)
	into STRICT	qt_conta_w
	from	pls_carencia
	where	nr_seq_contrato		= nr_seq_contrato_p
	and	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p;

	if (qt_conta_w	= 0) then
		insert into pls_carencia(nr_sequencia,nm_usuario,dt_atualizacao,
			nr_seq_contrato,nr_seq_tipo_carencia,qt_dias,ie_origem_carencia_benef)
		values (nextval('pls_carencia_seq'),nm_usuario_p, clock_timestamp(),
			nr_seq_contrato_p,nr_seq_tipo_carencia_p,qt_dia_p,'P');
	end if;
end if;

commit;

end;
$body$
