SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_inserir_cep_area_comercial ( nr_seq_area_p bigint, sg_estado_p text, nr_seq_localidade_p text, nr_seq_bairro_p text, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_cep_w			varchar(8);
nm_logradouro_w			varchar(125);
ds_bairro_w			varchar(80);
ds_uf_w				cep_log.ds_uf%type;
ds_municipio_w			varchar(255);

C01 CURSOR FOR
	SELECT	substr(a.cd_cep,1,8),
		a.nm_logradouro,
		substr(obter_desc_cep_bairro(a.cd_bairro_inicial,a.nr_seq_loc),1,80),
		a.ds_uf,
		substr(obter_desc_cep_loc(a.cd_cep),1,240)
	from	cep_log a
	where (a.ds_uf		= coalesce(sg_estado_p,a.ds_uf))
	and (a.nr_seq_loc		= coalesce(nr_seq_localidade_p,a.nr_seq_loc))
	and (a.cd_bairro_inicial	= coalesce(nr_seq_bairro_p,a.cd_bairro_inicial))
	and	not exists (	SELECT	1
				from	pls_area_atuacao_local x
				where	a.cd_cep	= x.cd_cep)
	and	ie_opcao_p	= 'I'
	
UNION ALL

	select	substr(a.cd_logradouro,1,8),
		a.nm_logradouro,
		a.nm_bairro,
		a.cd_unidade_federacao,
		a.nm_localidade
	from	cep_logradouro a
	where	a.cd_unidade_federacao	= coalesce(sg_estado_p,a.cd_unidade_federacao)
	and	not exists (	select	1
				from	pls_area_atuacao_local x
				where	a.cd_logradouro	= x.cd_cep)
	and	ie_opcao_p	= 'M';


BEGIN

open C01;
loop
fetch C01 into
	cd_cep_w,
	nm_logradouro_w,
	ds_bairro_w,
	ds_uf_w,
	ds_municipio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into pls_area_atuacao_local(	nr_sequencia, nr_seq_area, dt_atualizacao, nm_usuario,
						dt_atualizacao_nrec, nm_usuario_nrec, ie_situacao, cd_cep,
						ds_endereco, ds_bairro, ds_municipio, sg_estado)
					values (	nextval('pls_area_atuacao_local_seq'), nr_seq_area_p, clock_timestamp(), nm_usuario_p,
						clock_timestamp(), nm_usuario_p, 'A', cd_cep_w,
						nm_logradouro_w, ds_bairro_w, ds_municipio_w, ds_uf_w);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
