CREATE OR REPLACE PROCEDURE pls_inserir_conferencia_sib ( nr_seq_lote_sib_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_segurado_w		bigint;
ie_opcao_w			smallint;
nr_seq_segurado_ans_w		bigint;
ie_tipo_reg_reenvio_w		bigint;
nr_seq_motivo_reenvio_w		bigint;
nr_seq_segurado_tasy_w		bigint;
ie_carteira_ant_sib_w		varchar(1);

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.ie_opcao_sib ie_opcao, 
		a.ie_tipo_reg_reenvio, 
		a.nr_seq_motivo_reenvio, 
		ie_carteira_ant_sib 
	FROM pls_contrato d, pessoa_fisica b, pls_segurado a
LEFT OUTER JOIN pls_segurado_carteira c ON (a.nr_sequencia = c.nr_seq_segurado)
WHERE a.cd_pessoa_fisica	= b.cd_pessoa_fisica  and d.nr_sequencia	= a.nr_seq_contrato and coalesce(a.nr_cco::text, '') = '' and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and d.ie_tipo_beneficiario	= 'BE' and d.cd_estabelecimento	= cd_estabelecimento_p and a.ie_reenviar_sib	in ('A','T') and ie_tipo_segurado in ('B','R') 
	 
union
 
	SELECT	a.nr_sequencia, 
		a.ie_opcao, 
		a.ie_tipo_reg_reenvio, 
		a.nr_seq_motivo_reenvio, 
		a.ie_carteira_ant 
	from	pls_retorno_sib a 
	where	a.ie_enviar_sib	in ('A','T') 
	and	coalesce(nr_seq_lote_envio::text, '') = '' 
	order by ie_opcao;
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_segurado_w, 
	ie_opcao_w, 
	ie_tipo_reg_reenvio_w, 
	nr_seq_motivo_reenvio_w, 
	ie_carteira_ant_sib_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	if (ie_opcao_w in ('1','2')) then 
		nr_seq_segurado_ans_w	:= nr_seq_segurado_w;
		nr_seq_segurado_tasy_w	:= null;
	else 
		nr_seq_segurado_ans_w	:= null;
		nr_seq_segurado_tasy_w	:= nr_seq_segurado_w;
	end if;
	 
	 
	CALL pls_reenviar_sib(nr_seq_lote_sib_p,nr_seq_segurado_ans_w,ie_tipo_reg_reenvio_w, 
			nr_seq_motivo_reenvio_w,ie_opcao_w,nr_seq_segurado_tasy_w, 
			nm_usuario_p,cd_estabelecimento_p,ie_carteira_ant_sib_w);
	 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
