CREATE OR REPLACE PROCEDURE pls_inserir_contato_tit_resc ( nr_seq_solicitacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	b.cd_pessoa_fisica
	from	pls_solic_rescisao_benef a,
		pls_segurado b
	where	b.nr_sequencia	= a.nr_seq_segurado
	and	coalesce(b.nr_seq_titular::text, '') = ''
	and	a.nr_seq_solicitacao = nr_seq_solicitacao_p
	and	not exists (	SELECT	1
				from	pls_solic_resc_contato x
				where	x.nr_seq_solicitacao	= a.nr_seq_solicitacao
				and	x.cd_pessoa_fisica	= b.cd_pessoa_fisica);

BEGIN

for r_c01_w in C01 loop
	begin
	CALL pls_inserir_contato_solic_resc(nr_seq_solicitacao_p, r_c01_w.cd_pessoa_fisica, 'N', nm_usuario_p);
	end;
end loop;

CALL pls_gerar_contato_resc_estip(nr_seq_solicitacao_p, 'N', nm_usuario_p);

commit;

end;
$body$
