CREATE OR REPLACE PROCEDURE pls_inserir_cooperado_lote ( nr_seq_cooperado_p bigint, nr_seq_lote_p bigint, ie_selecionado_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_cooperado_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
/* ie_selecionado_p
0 - excluir
1 - incluir */
BEGIN
select	cd_pessoa_fisica,
	cd_cgc
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_w
from 	pls_cooperado
where	nr_sequencia = nr_seq_cooperado_p;

select	count(*)
into STRICT	qt_cooperado_w
from	pls_lote_escrit_quota a,
	pls_cooperado b,
	pls_lote_escrit_quota_item c
where	c.nr_seq_cooperado = b.nr_sequencia
and	c.nr_seq_lote = a.nr_sequencia
and	a.nr_sequencia = nr_seq_lote_p
and	((b.cd_pessoa_fisica = cd_pessoa_fisica_w) or (b.cd_cgc = cd_cgc_w));

if (ie_selecionado_p = 1) and (qt_cooperado_w = 0) then
	insert into pls_lote_escrit_quota_item(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_lote,
					nr_seq_cooperado)
			values (	nextval('pls_lote_escrit_quota_item_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_lote_p,
					nr_seq_cooperado_p);

elsif (ie_selecionado_p = 0) then
	delete from pls_lote_escrit_quota_item where nr_seq_lote = nr_seq_lote_p and nr_seq_cooperado = nr_seq_cooperado_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
