CREATE OR REPLACE PROCEDURE pls_inserir_cotacao ( nr_seq_auditoria_p pls_cotacao.nr_seq_auditoria%type, cd_perfil_p pls_cotacao.cd_perfil%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_cotacao_p INOUT bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar uma nova cotação a partir de uma análise
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_requisicao_w		pls_auditoria.nr_seq_requisicao%type;
nr_seq_guia_w			pls_auditoria.nr_seq_guia%type;

nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
nr_seq_plano_w			pls_plano.nr_sequencia%type;
nr_seq_prestador_exec_w		pls_prestador.nr_sequencia%type;
ds_observacao_w			varchar(4000);
dt_internacao_sugerida_w	timestamp;


BEGIN

if (nr_seq_auditoria_p IS NOT NULL AND nr_seq_auditoria_p::text <> '') then

	select	nr_seq_requisicao,
		nr_seq_guia
	into STRICT	nr_seq_requisicao_w,
		nr_seq_guia_w
	from	pls_auditoria
	where	nr_sequencia	= nr_seq_auditoria_p;

	if (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then

		select	nr_seq_segurado,
			nr_seq_plano,
			nr_seq_prestador_exec,
			ds_observacao,
			dt_internacao_sugerida
		into STRICT	nr_seq_segurado_w,
			nr_seq_plano_w,
			nr_seq_prestador_exec_w,
			ds_observacao_w,
			dt_internacao_sugerida_w
		from	pls_requisicao
		where	nr_sequencia	= nr_seq_requisicao_w;

	elsif (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then

		select	nr_seq_segurado,
			nr_seq_plano,
			nr_seq_prestador,
			ds_observacao,
			dt_internacao_sugerida
		into STRICT	nr_seq_segurado_w,
			nr_seq_plano_w,
			nr_seq_prestador_exec_w,
			ds_observacao_w,
			dt_internacao_sugerida_w
		from	pls_guia_plano
		where	nr_sequencia	= nr_seq_guia_w;
	end if;

	insert into pls_cotacao(
			nr_sequencia, dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec, dt_cotacao,
			ie_status, nr_seq_auditoria, nr_seq_segurado,
			nr_seq_plano, cd_perfil, nr_seq_prestador_exec,
			ds_observacao, dt_internacao_sugerida, cd_estabelecimento)
	values (
			nextval('pls_cotacao_seq'), clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p, clock_timestamp(),
			1, nr_seq_auditoria_p, nr_seq_segurado_w,
			nr_seq_plano_w, cd_perfil_p, nr_seq_prestador_exec_w,
			ds_observacao_w, dt_internacao_sugerida_w, cd_estabelecimento_p) returning nr_sequencia into nr_seq_cotacao_p;

	update	pls_auditoria
	set	ie_status	= 'EC',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_auditoria_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
