CREATE OR REPLACE PROCEDURE pls_inserir_data_fim_pacote ( dt_referencia_p timestamp, nr_sequencia_p bigint) AS $body$
BEGIN
if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '')	then 
	update 	pls_pacote_tipo_acomodacao 
	set 	dt_fim_vigencia = dt_referencia_p, 
		ie_situacao	= 'I'/* Rotina chamada no botão direito inativar composição no pacote, como solicitado na OS 358537 a ritina deve inativar a situação da regra também*/
 
	where 	nr_sequencia 	= nr_sequencia_p;
else 
	update 	pls_pacote_tipo_acomodacao 
	set 	ie_situacao	= 'I' 
	where 	nr_sequencia 	= nr_sequencia_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
