CREATE OR REPLACE PROCEDURE pls_inserir_divergencia_sib ( nr_seq_retorno_sib_p bigint, nr_seq_divergencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_divergencia_w	varchar(255);
ie_erro_w		varchar(10) := 'N';


BEGIN

if (nr_seq_divergencia_p IS NOT NULL AND nr_seq_divergencia_p::text <> '') then
	begin
	select	max(ds_divergencia)
	into STRICT	ds_divergencia_w
	from	sib_conferencia_diverg
	where	nr_sequencia	= nr_seq_divergencia_p;
	exception
	when others then
		ds_divergencia_w	:= '';
		ie_erro_w		:= 'S';
	end;

	if (ie_erro_w = 'N') then
		insert into pls_retorno_sib_diverg(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				cd_estabelecimento,nr_seq_retorno_sib,nr_seq_divergencia,ds_divergencia)
		values (	nextval('pls_retorno_sib_diverg_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				cd_estabelecimento_p,nr_seq_retorno_sib_p,nr_seq_divergencia_p,ds_divergencia_w);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
