CREATE OR REPLACE PROCEDURE pls_inserir_evento_pessoa ( nr_seq_segurado_p bigint, nr_seq_prestador_exec_p bigint, nr_seq_evento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_evento_pessoa_w		bigint;
cd_pessoa_fisica_w		varchar(10);
ds_cabecalho_w			varchar(4000);
nr_seq_evento_pessoa_w		bigint;


BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	pls_segurado 
where	nr_sequencia = nr_seq_segurado_p;
 
select	count(*) 
into STRICT	qt_evento_pessoa_w 
from	pls_evento_pessoa_portal 
where	cd_pessoa_fisica	= cd_pessoa_fisica_w 
and	nr_seq_evento 		= nr_seq_evento_p;
 
if (coalesce(qt_evento_pessoa_w,0) = 0) then 
	select	max(ds_cabecalho) 
	into STRICT	ds_cabecalho_w 
	from	pls_evento_portal_web 
	where	nr_sequencia	= nr_seq_evento_p;
 
	select	nextval('pls_evento_pessoa_portal_seq') 
	into STRICT	nr_seq_evento_pessoa_w 
	;
	 
	insert into pls_evento_pessoa_portal( 
		nr_sequencia, nr_seq_evento, cd_pessoa_fisica, 
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec, 
		nm_usuario_nrec, nr_seq_segurado, nr_seq_prestador, 
		ie_resposta, nr_seq_prestador_exec, ds_cabecalho) 
	values (nr_seq_evento_pessoa_w, nr_seq_evento_p, cd_pessoa_fisica_w, 
		clock_timestamp(), nm_usuario_p, clock_timestamp(), 
		nm_usuario_p, nr_seq_segurado_p, nr_seq_prestador_exec_p, 
		'P', nr_seq_prestador_exec_p, ds_cabecalho_w);
		 
	CALL pls_atualizar_macro_evento(nr_seq_evento_pessoa_w, nm_usuario_p);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
