SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_inserir_grupo_aud ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint, nr_seq_ordem_p bigint, ds_observacao_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_grupo_criado_p INOUT bigint) AS $body$
DECLARE


ie_status_w			varchar(1);
ie_atualiza_ordem_w		varchar(1)	:= 'S';
qt_grupos_finalizados_w		bigint;
nr_seq_aud_conta_grupo_w	bigint;
nr_seq_ordem_w			bigint;
nr_seq_ordem_ww			bigint;
nr_seq_grupo_odem_exist_w	bigint;
nr_sequencia_w			bigint;
nr_seq_grupo_pre_analise_w	bigint;
ds_observacao_w			varchar(4000);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_ordem
	from	pls_auditoria_conta_grupo
	where	nr_seq_analise	= nr_seq_analise_p
	and	nr_seq_ordem >= nr_seq_ordem_p
	order by
		nr_seq_ordem;


BEGIN
begin
select	max(a.nr_seq_grupo_pre_analise)
into STRICT	nr_seq_grupo_pre_analise_w
from	pls_parametros	a
where	a.cd_estabelecimento	= cd_estabelecimento_p;
exception
when others then
	nr_seq_grupo_pre_analise_w	:= null;
end;

ds_observacao_w	:= substr(ds_observacao_p,1,4000);

if (nr_seq_grupo_p = nr_seq_grupo_pre_analise_w) then
	nr_seq_ordem_w		:= 0;
	ie_atualiza_ordem_w	:= 'N';
else
	nr_seq_ordem_w		:= nr_seq_ordem_p + 1;
	ie_atualiza_ordem_w	:= 'S';
end if;

select	nextval('pls_auditoria_conta_grupo_seq')
into STRICT	nr_sequencia_w
;

insert into pls_auditoria_conta_grupo(nr_sequencia,
	nr_seq_analise,
	nr_seq_grupo,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_ordem,
	ie_manual,
	ds_observacao,
	ie_pre_analise)
values (nr_sequencia_w,
	nr_seq_analise_p,
	nr_seq_grupo_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_ordem_w,
	'S',
	ds_observacao_w,
	'N');

if (ie_atualiza_ordem_w = 'S') then
	CALL pls_atualiza_ordem_grupos(nr_sequencia_w, nr_seq_ordem_p + 1, nr_seq_analise_p, cd_estabelecimento_p, nm_usuario_p);
end if;

select 	count(nr_sequencia)
into STRICT	qt_grupos_finalizados_w
from 	pls_auditoria_conta_grupo
where	nr_Seq_analise	= nr_seq_analise_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

/*Se existir um grupo finalizado (ou seja com data de liberação) se coloca a análise em em auditoria senão em aguardando auditoria*/

if (qt_grupos_finalizados_w = 0) then
	ie_status_w := 'G';
else
	ie_status_w := 'A';
end if;

CALL pls_alterar_status_analise_cta(nr_seq_analise_p, ie_status_w, 'PLS_INSERIR_GRUPO_AUD', nm_usuario_p, cd_estabelecimento_p);

update	pls_analise_conta
set	ie_auditoria	= 'S'
where	nr_Sequencia 	= nr_seq_analise_p;

CALL pls_atualizar_grupo_penden(nr_seq_analise_p, cd_estabelecimento_p, nm_usuario_p);

nr_seq_grupo_criado_p	:= nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
