CREATE OR REPLACE PROCEDURE pls_inserir_hist_analise (nr_seq_conta_p bigint, nr_seq_analise_p bigint, ie_tipo_historico_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text, nr_seq_ocorrencia_p bigint, nr_seq_glosa_p bigint, ds_observacao_P text, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_conta_proc_w	pls_conta_proc.nr_sequencia%type := null;
nr_seq_conta_mat_w	pls_conta_mat.nr_sequencia%type := null;


BEGIN

--Se tipo histórico for 25 ou 26, então é da discussão e a sequência do item é oriunda da w_pls_discussao_item
if (ie_tipo_historico_p not in (25,26,27)) then

	if (ie_tipo_item_p = 'P') then

		nr_seq_conta_proc_w := nr_seq_item_p;

	elsif (ie_tipo_item_p = 'M') then

		nr_seq_conta_mat_w := nr_seq_item_p;

	end if;

end if;

if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') then
	insert into pls_hist_analise_conta(nr_sequencia, nr_seq_conta, nr_seq_analise,
		 ie_tipo_historico, dt_atualizacao, nm_usuario,
		 dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_item,
		 ie_tipo_item, nr_seq_ocorrencia, ds_observacao,
		 nr_seq_glosa, nr_seq_grupo, nr_seq_conta_proc,
		 nr_seq_conta_mat)
	values (nextval('pls_hist_analise_conta_seq'), nr_seq_conta_p, nr_seq_analise_p,
		 ie_tipo_historico_p, clock_timestamp(), nm_usuario_p,
		 clock_timestamp(), nm_usuario_p, nr_seq_item_p,
		 ie_tipo_item_p, nr_seq_ocorrencia_p, ds_observacao_p,
		 nr_seq_glosa_p, nr_seq_grupo_p, nr_seq_conta_proc_w,
		 nr_seq_conta_mat_w);
end if;

end;
$body$
