CREATE OR REPLACE PROCEDURE pls_inserir_itens_cotacao ( nr_seq_cotacao_p bigint, ds_hist_cotacao_itens_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_material_p bigint, nr_seq_auditoria_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Inserir os itens na cotação
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_item_cotacao_w		pls_item_cotacao.nr_sequencia%type;
nr_seq_prest_fornec_w		pls_auditoria_item.nr_seq_prest_fornec%type;
nr_seq_material_forn_w		pls_auditoria_item.nr_seq_material_forn%type;
qt_ajuste_w			pls_auditoria_item.qt_ajuste%type;
qt_original_w			pls_auditoria_item.qt_original%type;
ie_status_analise_w		pls_auditoria_item.ie_status%type;


BEGIN

if (nr_seq_cotacao_p IS NOT NULL AND nr_seq_cotacao_p::text <> '') then

	select	nr_seq_prest_fornec,
		nr_seq_material_forn,
		qt_ajuste,
		qt_original,
		ie_status
	into STRICT	nr_seq_prest_fornec_w,
		nr_seq_material_forn_w,
		qt_ajuste_w,
		qt_original_w,
		ie_status_analise_w
	from	pls_auditoria_item
	where	nr_sequencia	= nr_seq_auditoria_item_p;


	select	nextval('pls_item_cotacao_seq')
	into STRICT	nr_seq_item_cotacao_w
	;

	insert into pls_item_cotacao(
			nr_sequencia, nr_seq_cotacao, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			nr_seq_aud_item, nr_seq_material, cd_procedimento,
			ie_origem_proced, nr_seq_prest_fornec, nr_seq_material_forn,
			ie_status, qt_item, ie_status_analise)
	values (
			nr_seq_item_cotacao_w, nr_seq_cotacao_p, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			nr_seq_auditoria_item_p, nr_seq_material_p, cd_procedimento_p,
			ie_origem_proced_p, nr_seq_prest_fornec_w, nr_seq_material_forn_w,
			1, coalesce(qt_ajuste_w,qt_original_w), ie_status_analise_w);

	if (ds_hist_cotacao_itens_p IS NOT NULL AND ds_hist_cotacao_itens_p::text <> '') then

		insert into pls_item_cotacao_hist(
				nr_sequencia, dt_atualizacao, ie_tipo_historico,
				nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_item_cotacao, ds_historico)
		values (
				nextval('pls_item_cotacao_hist_seq'), clock_timestamp(), 0,
				nm_usuario_p, clock_timestamp(), nm_usuario_p,
				nr_seq_item_cotacao_w, ds_hist_cotacao_itens_p);
	end if;

	update	pls_auditoria_item
	set	ie_status	= 'E',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_auditoria_item_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
