SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_inserir_itens_execusao_req ( ie_tipo_item_p text, nr_seq_segurado_p bigint, qt_item_exec_p bigint, nr_seq_requisicao_p bigint, nr_seq_req_execucao_p bigint, cd_medico_executor_p text, nr_seq_req_proc_p bigint, nr_seq_req_mat_p bigint, nm_usuario_p text, ie_tipo_atendimento_p text) AS $body$
DECLARE



nr_seq_material_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ie_tipo_guia_w			integer;
cd_medico_w			varchar(10);
nr_seq_grupo_exec_w		bigint;
ie_tipo_processo_w		varchar(1);
nr_seq_preco_pacote_w		bigint;
nr_seq_conversao_pacote_w	bigint;
cd_pacote_item_w		bigint;
nr_seq_pacote_w			bigint;
ie_tipo_anexo_w			pls_requisicao_proc.ie_tipo_anexo%type;
ie_tipo_atendimento_w		varchar(2);


BEGIN


if (ie_tipo_item_p = 'P') then
	select	a.ie_tipo_guia,
		a.cd_medico_solicitante,
		coalesce(a.ie_tipo_processo, CASE WHEN a.ie_tipo_requisicao='W' THEN  'P'  ELSE 'M' END ),
		coalesce(b.nr_seq_grupo_exec,0),
		b.cd_procedimento,
		b.ie_origem_proced,
		nr_seq_preco_pacote,
		nr_seq_conversao_pacote,
		cd_pacote_item,
		b.ie_tipo_anexo,
		b.nr_seq_pacote,
		a.ie_tipo_atendimento
	into STRICT	ie_tipo_guia_w,
		cd_medico_w,
		ie_tipo_processo_w,
		nr_seq_grupo_exec_w,
		cd_procedimento_w,
		ie_origem_proced_w,
		nr_seq_preco_pacote_w,
		nr_seq_conversao_pacote_w,
		cd_pacote_item_w,
		ie_tipo_anexo_w,
		nr_seq_pacote_w,
		ie_tipo_atendimento_w
	from	pls_requisicao_proc 	b,
		pls_requisicao		a
	where	a.nr_sequencia	= b.nr_seq_requisicao
	and	b.nr_sequencia	= nr_seq_req_proc_p;

	/* Update utilizado para controlar a execução dos itens da requisição CE - Consistindo Execução, quando for finalizada a consistência da requisição os itens voltam para L - Liberado*/

	update 	pls_requisicao_proc
	set	ie_estagio_execucao = 'CE',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia = nr_seq_req_proc_p;

elsif (ie_tipo_item_p = 'M') then
	select	a.ie_tipo_guia,
		a.cd_medico_solicitante,
		coalesce(a.ie_tipo_processo, CASE WHEN a.ie_tipo_requisicao='W' THEN  'P'  ELSE 'M' END ),
		coalesce(b.nr_seq_grupo_exec,0),
		b.nr_seq_material,
		b.ie_tipo_anexo,
		a.ie_tipo_atendimento
	into STRICT	ie_tipo_guia_w,
		cd_medico_w,
		ie_tipo_processo_w,
		nr_seq_grupo_exec_w,
		nr_seq_material_w,
		ie_tipo_anexo_w,
		ie_tipo_atendimento_w
	from	pls_requisicao_mat	b,
		pls_requisicao		a
	where	a.nr_sequencia	= b.nr_seq_requisicao
	and	b.nr_sequencia	= nr_seq_req_mat_p;

	/* Update utilizado para controlar a execução dos itens da requisição CE - Consistindo Execução, quando for finalizada a consistência da requisição os itens voltam para L - Liberado*/

	update 	pls_requisicao_mat
	set	ie_estagio_execucao = 'CE',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia = nr_seq_req_mat_p;

end if;

insert	into pls_execucao_req_item(nr_sequencia, ie_tipo_guia, nr_seq_grupo_exec,
	nr_seq_segurado, dt_atualizacao, nm_usuario,
	cd_procedimento, ie_origem_proced, nr_seq_material,
	qt_item, nr_seq_req_proc, nr_seq_req_mat,
	cd_medico_requisitante, ie_tipo_processo, nr_seq_requisicao,
	nr_seq_execucao, nr_seq_preco_pacote, nr_seq_conversao_pacote,
	cd_pacote_item, cd_medico_executor, ie_situacao,
	ie_tipo_anexo, ie_tipo_atendimento, nr_seq_pacote)
values (nextval('pls_execucao_req_item_seq'), ie_tipo_guia_w, nr_seq_grupo_exec_w,
	nr_seq_segurado_p, clock_timestamp(), nm_usuario_p,
	cd_procedimento_w, ie_origem_proced_w, nr_seq_material_w,
	qt_item_exec_p, nr_seq_req_proc_p,nr_seq_req_mat_p,
	cd_medico_w, ie_tipo_processo_w, nr_seq_requisicao_p,
	nr_seq_req_execucao_p, nr_seq_preco_pacote_w, nr_seq_conversao_pacote_w,
	cd_pacote_item_w, cd_medico_executor_p, 'U',
	ie_tipo_anexo_w, coalesce(ie_tipo_atendimento_p, ie_tipo_atendimento_w),nr_seq_pacote_w);


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
