SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_inserir_justif_analise ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, ds_mensagem_p pls_regra_cta_just_aut.ds_mensagem%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Solicitar uma justificativa para a analise de forma manual
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/BEGIN

if (coalesce(nr_seq_conta_p::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(386250);
end if;

if (coalesce(nr_seq_analise_p::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(386257);
end if;

if (coalesce(ds_mensagem_p::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(386248);
end if;

if	((coalesce(nr_seq_conta_proc_p::text, '') = '') and (coalesce(nr_seq_conta_mat_p::text, '') = '')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(386748);
end if;

if (nr_seq_analise_p IS NOT NULL AND nr_seq_analise_p::text <> '') and (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') and (ds_mensagem_p IS NOT NULL AND ds_mensagem_p::text <> '') and
	((nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') or (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> ''))then

	insert into pls_hist_analise_conta(	nr_sequencia,
						nr_seq_conta,
						nr_seq_analise,
						ie_tipo_historico,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_item,
						ie_tipo_item,
						nr_seq_ocorrencia,
						ds_observacao,
						nr_seq_glosa,
						nr_seq_grupo,
						nr_seq_conta_proc,
						nr_seq_conta_mat,
						nr_seq_proc_partic,
						nr_seq_ocor_benef,
						nr_seq_conta_glosa)
	SELECT	nextval('pls_hist_analise_conta_seq'),
		nr_seq_conta_p,
		nr_seq_analise_p,
		'36', -- Aguardando justificativa médica
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		null,
		ds_mensagem_p,
		null,
		null,
		nr_seq_conta_proc_p,
		nr_seq_conta_mat_p,
		null,
		null,
		null
	;
	
	-- atualiza o status da conta
	update	pls_conta
	set	ie_analise_justificativa	= 'A'
	where	nr_sequencia			= nr_seq_conta_p;
	
	-- atualiza o status do procedimento
	if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
		update	pls_conta_proc
		set	ie_status	= 'J'
		where	nr_sequencia	= nr_seq_conta_proc_p;
	end if;
	
	-- atualiza o stauts do material
	if (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
		update	pls_conta_mat
		set	ie_status	= 'J'
		where	nr_sequencia	= nr_seq_conta_mat_p;
	end if;
	
	-- atualiza o status da analise
	update	pls_analise_conta
	set	ie_status	= 'J' -- Aguardando justificativa
	where	nr_sequencia	= nr_seq_analise_p;
		
	--Gera pendência de notificação do Portal
	CALL pls_gerar_notificacao_tws(nm_usuario_p, 'JC', 'P', nr_seq_conta_p);
					
	if (ie_commit_p = 'S') then
		commit;	
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_inserir_justif_analise ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, ds_mensagem_p pls_regra_cta_just_aut.ds_mensagem%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) FROM PUBLIC;

