CREATE OR REPLACE PROCEDURE pls_inserir_lanc_susp_faixa ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, vl_item_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, nr_seq_motivo_p pls_tipo_lanc_adic.nr_sequencia%type, nr_seq_mens_item_p pls_mensalidade_seg_item.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


dt_inicio_w	timestamp;
dt_fim_w	timestamp;
					

BEGIN

dt_inicio_w	:= trunc(dt_inicio_p,'mm');
dt_fim_w	:= trunc(dt_fim_p,'mm');

if (coalesce(nr_seq_motivo_p::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1129704);
elsif (dt_fim_w < dt_inicio_w) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1129690);
elsif (vl_item_p > 0) then
	while dt_inicio_w <= dt_fim_w loop
		begin
		insert into pls_lancamento_mensalidade(nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
			nm_usuario, nm_usuario_nrec, dt_mes_competencia,
			dt_ocorrencia, ie_situacao, ie_tipo_item,
			nr_seq_segurado, vl_lancamento, dt_liberacao,
			nm_usuario_liberacao, nr_seq_motivo, cd_estabelecimento,
			nr_seq_mensalidade_item)
		values (nextval('pls_lancamento_mensalidade_seq'), clock_timestamp(), clock_timestamp(),
			nm_usuario_p, nm_usuario_p, dt_inicio_w,
			clock_timestamp(), 'A', '20',
			nr_seq_segurado_p, vl_item_p, clock_timestamp(),
			nm_usuario_p, nr_seq_motivo_p, cd_estabelecimento_p,
			nr_seq_mens_item_p);
		
		dt_inicio_w	:= add_months(dt_inicio_w, 1);
		end;
	end loop;
end if;

commit;

end;
$body$
