CREATE OR REPLACE PROCEDURE pls_inserir_liminar_excecao ( nr_seq_segurado_p bigint, dt_solicitacao_p timestamp, nr_seq_requisicao_p bigint, nr_seq_guia_p bigint, ie_tipo_item_p bigint, nm_usuario_p text) AS $body$
DECLARE

				
cd_juridico_liminar_w		varchar(20);
nr_seq_liminar_w		bigint;
qt_reg_w			integer;
nr_seq_contrato_w		bigint;


BEGIN

begin
	select	nr_seq_contrato
	into STRICT	nr_seq_contrato_w
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p;
exception
when others then
	nr_seq_contrato_w := 0;
end;

select	max(nr_sequencia)
into STRICT	nr_seq_liminar_w
from	processo_judicial_liminar
where (nr_seq_segurado	= nr_seq_segurado_p
or	nr_seq_contrato		= nr_seq_contrato_w)
and	ie_estagio		= 2
and	ie_impacto_autorizacao	= 'S'
and	dt_solicitacao_p between ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicio_validade) and fim_dia(coalesce(dt_fim_validade,dt_solicitacao_p));

if (coalesce(nr_seq_liminar_w,0) > 0) then
	select	substr(cd_juridico_liminar,1,20)
	into STRICT	cd_juridico_liminar_w
	from	processo_judicial_liminar
	where	nr_sequencia = nr_seq_liminar_w;
	
	if (ie_tipo_item_p in (5,6,9)) then
		select	count(1)
		into STRICT	qt_reg_w
		from	pls_req_liminar_judicial
		where	nr_seq_processo		= nr_seq_liminar_w
		and	nr_seq_requisicao 	= nr_seq_requisicao_p;
		
		if (qt_reg_w = 0) then
			insert into pls_req_liminar_judicial(nr_sequencia, nr_liminar, nr_seq_processo,
				nr_seq_requisicao, dt_atualizacao, nm_usuario,
				dt_atualizacao_nrec, nm_usuario_nrec)
			values (nextval('pls_req_liminar_judicial_seq'), '', nr_seq_liminar_w,
				nr_seq_requisicao_p, clock_timestamp(), nm_usuario_p,
				clock_timestamp(), nm_usuario_p);
		end if;
	elsif (ie_tipo_item_p in (1,2,7)) then
		select	count(1)
		into STRICT	qt_reg_w
		from	pls_guia_liminar_judicial
		where	nr_seq_processo		= nr_seq_liminar_w
		and	nr_seq_segurado 	= nr_seq_segurado_p;

		if (qt_reg_w = 0) then
			insert into pls_guia_liminar_judicial(nr_sequencia, nr_liminar, nr_seq_processo,
				nr_seq_guia, dt_atualizacao, nm_usuario,
				dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_segurado)
			values (nextval('pls_guia_liminar_judicial_seq'), '', nr_seq_liminar_w,
				nr_seq_guia_p, clock_timestamp(), nm_usuario_p,
				clock_timestamp(), nm_usuario_p, nr_seq_segurado_p);
		end if;
	end if;
end if;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_inserir_liminar_excecao ( nr_seq_segurado_p bigint, dt_solicitacao_p timestamp, nr_seq_requisicao_p bigint, nr_seq_guia_p bigint, ie_tipo_item_p bigint, nm_usuario_p text) FROM PUBLIC;

