CREATE OR REPLACE PROCEDURE pls_inserir_liminar_requisicao ( nr_seq_liminar_p processo_judicial_liminar.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Inserir a liminar judicial na guia ou requisição.

Aprocedure está com este nome pois anteriormente era usada somente para requisição.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
				
cd_juridico_liminar_w		processo_judicial_liminar.cd_juridico_liminar%type;
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;


BEGIN

if (nr_seq_liminar_p IS NOT NULL AND nr_seq_liminar_p::text <> '') then
	if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
		select	cd_juridico_liminar
		into STRICT	cd_juridico_liminar_w
		from	processo_judicial_liminar
		where	nr_sequencia = nr_seq_liminar_p;
		
		insert	into pls_req_liminar_judicial(nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
			 nm_usuario, nm_usuario_nrec, nr_seq_processo,
			 nr_seq_requisicao, nr_liminar)
		values (nextval('pls_req_liminar_judicial_seq'), clock_timestamp(), clock_timestamp(),
			 nm_usuario_p, nm_usuario_p, nr_seq_liminar_p,
			 nr_seq_requisicao_p, substr(cd_juridico_liminar_w,1,20));
	elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
		begin
			select	nr_seq_segurado
			into STRICT	nr_seq_segurado_w
			from	pls_guia_plano
			where	nr_sequencia	= nr_seq_guia_p;
		exception
		when others then
			nr_seq_segurado_w	:= null;
		end;
		
		select	coalesce(cd_juridico_liminar,'')
		into STRICT	cd_juridico_liminar_w
		from	processo_judicial_liminar
		where	nr_sequencia = nr_seq_liminar_p;
		
		insert	into pls_guia_liminar_judicial(nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
			 nm_usuario, nm_usuario_nrec, nr_liminar,
			 nr_seq_guia, nr_seq_processo, nr_seq_segurado)
		values (nextval('pls_guia_liminar_judicial_seq'), clock_timestamp(), clock_timestamp(),
			 nm_usuario_p, nm_usuario_p, substr(cd_juridico_liminar_w,1,20),
			 nr_seq_guia_p, nr_seq_liminar_p, nr_seq_segurado_w);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
