SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_inserir_mat_req_web ( nr_seq_requisicao_p bigint, nr_seq_material_p bigint, qt_solicitado_p bigint, ie_utiliza_opme_p text, nm_usuario_p text, cd_estabelecimento_p bigint, vl_material_p bigint, ie_tipo_lanc_anexo_p text, nr_seq_req_mat_p INOUT bigint, nr_seq_fornecedor_p bigint, ds_observacao_p text) AS $body$
DECLARE

 
nr_seq_mat_w			bigint;
ie_tipo_intercambio_w		varchar(10);
ie_tipo_processo_w		varchar(1);
ie_tipo_lanc_anexo_w		varchar(2);
vl_material_w			pls_requisicao_mat.vl_material%type	 := null;
nr_seq_mat_fornec_preco_w	pls_material_fornec_preco.nr_sequencia%type;
nr_seq_fornecedor_w		pls_fornecedor_material.nr_sequencia%type := null;


BEGIN 
 
select 	nextval('pls_requisicao_mat_seq') 
into STRICT	nr_seq_mat_w
;
 
begin 
	select	ie_tipo_intercambio, 
			ie_tipo_processo 
	into STRICT	ie_tipo_intercambio_w, 
			ie_tipo_processo_w 
	from	pls_requisicao 
	where 	nr_sequencia = nr_seq_requisicao_p;
exception 
when others then 
	ie_tipo_intercambio_w := null;
	ie_tipo_processo_w := null;
end;
 
if (coalesce(nr_seq_fornecedor_p, 0) > 0) then 
	nr_seq_fornecedor_w	:= nr_seq_fornecedor_p;
	 
	select	max(b.nr_sequencia) 
	into STRICT	nr_seq_mat_fornec_preco_w 
	from	pls_material_fornec a, 
			pls_material_fornec_preco b 
	where	b.nr_seq_mat_fornec		= a.nr_sequencia 
	and 	a.nr_seq_fornecedor 	= nr_seq_fornecedor_w 
	and  	a.nr_seq_material 		= nr_seq_material_p 
	and		clock_timestamp() between b.dt_inicio_vigencia and coalesce(b.dt_fim_vigencia,clock_timestamp());
 
	if (coalesce(nr_seq_mat_fornec_preco_w, 0) > 0) then 
		begin 
			select	vl_preco 
			into STRICT	vl_material_w 
			from	pls_material_fornec_preco 
			where	nr_sequencia = nr_seq_mat_fornec_preco_w;
		exception 
		when others then 
			vl_material_w	:= null;
		end;
 
	end if;
elsif (vl_material_p IS NOT NULL AND vl_material_p::text <> '') then 
	vl_material_w	:= vl_material_p;
end if;
 
insert into pls_requisicao_mat(nr_sequencia, nr_seq_requisicao, nr_seq_material, 
	qt_solicitado, ie_estagio, ie_status, 
	dt_atualizacao, dt_atualizacao_nrec, nm_usuario, 
	ie_utiliza_opme, nm_usuario_nrec, qt_mat_executado, 
	qt_material, ie_origem_inclusao, vl_material, 
	nr_seq_material_forn, ds_observacao) 
values (nr_seq_mat_w, nr_seq_requisicao_p, nr_seq_material_p, 
	qt_solicitado_p, 'AE', 'U', 
	clock_timestamp(), clock_timestamp(), nm_usuario_p, 
	ie_utiliza_opme_p, nm_usuario_p, 0, 
	0, 'P', vl_material_w, 
	nr_seq_fornecedor_w, ds_observacao_p);
commit;
 
if (coalesce(vl_material_w::text, '') = '' and coalesce(nr_seq_fornecedor_w::text, '') = '') then 
	CALL pls_atualiza_valor_mat_aut(nr_seq_mat_w, 'R',nm_usuario_p);
end if;
 
 
--Verifica se o lançamento do anexo é manual ou por regra e caso seja por regra é verificado se o item possui algum tipo de anexo para então inserir o tipo de anexo no item 
if (ie_tipo_lanc_anexo_p IS NOT NULL AND ie_tipo_lanc_anexo_p::text <> '') and (ie_tipo_lanc_anexo_p = 'LR') then 
	CALL pls_atualizar_tipo_anexo_item(	null, nr_seq_mat_w, null, 
					null, null, null, 
					nr_seq_material_p, nm_usuario_p);
end if;
 
if (ie_tipo_intercambio_w = 'I' and ie_tipo_processo_w = 'I') then 
	CALL pls_inserir_obs_regra_intercam(nr_seq_mat_w, null, 'M', nm_usuario_p);
	CALL pls_gerar_de_para_req_intercam( null, nr_seq_mat_w, null, 
					null, null, null, 
					null, cd_estabelecimento_p, nm_usuario_p);
end if;
 
CALL pls_lanc_auto_requisicao(nr_seq_requisicao_p, null, nr_seq_mat_w, 
			 nm_usuario_p, cd_estabelecimento_p, ie_tipo_lanc_anexo_p);
 
nr_seq_req_mat_p := nr_seq_mat_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_inserir_mat_req_web ( nr_seq_requisicao_p bigint, nr_seq_material_p bigint, qt_solicitado_p bigint, ie_utiliza_opme_p text, nm_usuario_p text, cd_estabelecimento_p bigint, vl_material_p bigint, ie_tipo_lanc_anexo_p text, nr_seq_req_mat_p INOUT bigint, nr_seq_fornecedor_p bigint, ds_observacao_p text) FROM PUBLIC;

