CREATE OR REPLACE PROCEDURE pls_inserir_novas_carencias ( nr_seq_tipo_carencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_carencia_w		bigint;
qt_dias_padrao_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_carencia_w
from	w_pls_carencia
where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p;

select   max(qt_dias_padrao)
into STRICT	qt_dias_padrao_w
from     pls_tipo_carencia
where    nr_sequencia = nr_seq_tipo_carencia_p;

if (qt_carencia_w	= 0) then
	insert into w_pls_carencia(	nr_sequencia,cd_estabelecimento,dt_atualizacao,
			nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
			nr_seq_tipo_carencia,qt_dias_carencia,ie_origem_carencia,
			dt_inicio_vigencia	)
	values (	nextval('w_pls_carencia_seq'),cd_estabelecimento_p,clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			nr_seq_tipo_carencia_p,coalesce(qt_dias_padrao_w,0),'M',
			clock_timestamp());
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
