CREATE OR REPLACE PROCEDURE pls_inserir_nova_pendencia ( nr_sequencia_p pls_pendencias_prestador.nr_sequencia%type, nr_seq_chamado_p pls_pendencias_prestador.nr_seq_chamado%type, nr_seq_cnpj_prestador_p pls_pendencias_prestador.nr_seq_cnpj_prestador%type, nr_seq_tipo_solicitacao_p pls_pendencias_prestador.nr_seq_tipo_solicitacao%type, dt_prazo_p pls_pendencias_prestador.dt_prazo%type, nr_seq_estagio_p pls_pendencias_prestador.nr_seq_estagio%type, nr_seq_etapa_p pls_pendencias_prestador.nr_seq_etapa%type, nr_sequencial_pendencia_p pls_pendencias_prestador.nr_sequencial_pendencia%type, nm_usuario_p pls_pendencias_prestador.nm_usuario%type, nm_usuario_nrec_p pls_pendencias_prestador.nm_usuario_nrec%type, cd_perfil_p pls_pendencias_prestador.cd_perfil%type, ie_status_p pls_pendencias_prestador.ie_status%type ) AS $body$
BEGIN

    if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
        update pls_pendencias_prestador
        set dt_liberacao = clock_timestamp(),  
            ie_status = 3, 
            nm_usuario = nm_usuario_p,
            dt_atualizacao = clock_timestamp()  
        where nr_sequencia = nr_sequencia_p;
    end if;

    insert into pls_pendencias_prestador(
        nr_sequencia,
        nr_seq_chamado,
        nr_seq_cnpj_prestador,
        nr_seq_tipo_solicitacao,
        dt_prazo,
        dt_atualizacao,
        nr_seq_etapa,
        nr_seq_estagio,
        nr_sequencial_pendencia,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        cd_perfil,
        ie_status
    ) values (
        nextval('pls_pendencias_prestador_seq'),
        nr_seq_chamado_p,
        nr_seq_cnpj_prestador_p,
        nr_seq_tipo_solicitacao_p,
        dt_prazo_p,
        clock_timestamp(),
        nr_seq_etapa_p,
        nr_seq_estagio_p,
        nr_sequencial_pendencia_p,
        nm_usuario_p,
        clock_timestamp(),
        nm_usuario_nrec_p,
        cd_perfil_p,
        ie_status_p
    );

    update pls_chamados_solic set
        nr_seq_status_cred = nr_seq_estagio_p,
        dt_atualizacao = clock_timestamp(),
        nm_usuario = nm_usuario_p
    where nr_sequencia = nr_seq_chamado_p;

    commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
