CREATE OR REPLACE PROCEDURE pls_inserir_obito_pf ( cd_pessoa_fisica_p text, dt_obito_p timestamp, nr_certidao_obito_p text, nm_usuario_p text, ie_certidao_obito_p text) AS $body$
BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and
	(((nr_certidao_obito_p IS NOT NULL AND nr_certidao_obito_p::text <> '') and ie_certidao_obito_p = 'S') or (ie_certidao_obito_p = 'N')) then

	update	pessoa_fisica
	set	dt_obito		= dt_obito_p,
		nr_certidao_obito	= nr_certidao_obito_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;
end if;

end;
$body$
