CREATE OR REPLACE PROCEDURE pls_inserir_pagador_compl ( nr_seq_pagador_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_tipo_item_p text, nr_seq_pagador_item_p bigint, nr_seq_tipo_lanc_p bigint, nr_seq_grupo_itens_p bigint) AS $body$
DECLARE


C01 CURSOR(	nr_seq_grupo_item_pc	pls_grupo_item_mens.nr_sequencia%type) FOR
	SELECT	ie_tipo_item ie_tipo_item,
		CASE WHEN ie_tipo_item='20' THEN  nr_seq_tipo_lanc  ELSE null END  nr_seq_tipo_lanc
	from	pls_item_grupo_tipo_mens
	where	nr_seq_grupo_item = nr_seq_grupo_item_pc
	and	ie_tipo_item in ('1','3','6','12','13','20');
		
BEGIN
	
if (nr_seq_grupo_itens_p IS NOT NULL AND nr_seq_grupo_itens_p::text <> '') then
	for r_c01_w in c01(nr_seq_grupo_itens_p) loop
		begin
		insert	into	pls_pagador_item_mens(nr_sequencia,
				nr_seq_pagador,
				cd_estabelecimento,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_tipo_item,
				nr_seq_pagador_item,
				nr_seq_tipo_lanc)
			values (nextval('pls_pagador_item_mens_seq'),
				nr_seq_pagador_p,
				cd_estabelecimento_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				r_c01_w.ie_tipo_item,
				nr_seq_pagador_item_p,
				r_c01_w.nr_seq_tipo_lanc);
		end;
	end loop;			
else
	insert	into	pls_pagador_item_mens(nr_sequencia,
			nr_seq_pagador,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo_item,
			nr_seq_pagador_item,
			nr_seq_tipo_lanc)
		values (nextval('pls_pagador_item_mens_seq'),
			nr_seq_pagador_p,
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ie_tipo_item_p,
			nr_seq_pagador_item_p,
			nr_seq_tipo_lanc_p);
end if;
		
