SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_inserir_rescicao_pagador ( nr_seq_alteracao_motivo_p bigint, nr_seq_pagador_p bigint, nr_seq_segurado_p bigint, nr_seq_motivo_rescisao_pag_p bigint, nr_seq_causa_rescisao_p bigint, dt_alteracao_p timestamp, nm_usuario_p text, qt_contribuicao_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


dt_adesao_w			timestamp;
qt_minima_meses_w		bigint;
qt_maximo_meses_w		bigint;
tx_fracao_w			smallint;
qt_meses_incluido_w		integer;
qt_meses_lancados_w		bigint;
nr_seq_rescisao_pag_w		bigint;
nr_seq_causa_rescisao_w		bigint;
dt_rescisao_gerada_w		timestamp;
qt_rescisao_pagador_w		bigint;
nr_seq_mot_rescisao_w		bigint;
ds_erro_w			varchar(4000);
ie_situacao_trab_w		varchar(10);
dt_limite_utilizacao_w		timestamp;
ie_vencimento_fim_mes_w		pls_motivo_alt_pagador.ie_vencimento_fim_mes%type;



BEGIN

if (coalesce(nr_seq_causa_rescisao_p,0) <> 0) then
	nr_seq_causa_rescisao_w	:= nr_seq_causa_rescisao_p;
else
	nr_seq_causa_rescisao_w	:= null;
end if;


if (coalesce(nr_seq_motivo_rescisao_pag_p,0) <> 0) then
	nr_seq_mot_rescisao_w	:= nr_seq_motivo_rescisao_pag_p;
else
	select	max(nr_sequencia)
	into STRICT	nr_seq_mot_rescisao_w
	from	pls_motivo_cancelamento;
end if;

select	ie_situacao_trabalhista,
	coalesce(ie_vencimento_fim_mes,'N')
into STRICT	ie_situacao_trab_w,
	ie_vencimento_fim_mes_w
from	pls_motivo_alt_pagador
where	nr_sequencia	= nr_seq_alteracao_motivo_p
and	ie_situacao	= 'A';

select	count(1)
into STRICT	qt_rescisao_pagador_w
from	pls_rescisao_contrato
where	nr_seq_pagador	= nr_seq_pagador_p;

qt_meses_lancados_w := pls_obter_limite_resc_pagador(nr_seq_alteracao_motivo_p, nr_seq_segurado_p, dt_alteracao_p, qt_contribuicao_p);


if (qt_rescisao_pagador_w = 0) and (qt_meses_lancados_w > 0) then

	dt_rescisao_gerada_w	:= add_months(coalesce(dt_alteracao_p,clock_timestamp()),qt_meses_lancados_w);
	dt_limite_utilizacao_w	:= trunc(dt_rescisao_gerada_w,'dd') + 86399/86400; /*(last_day(trunc(dt_rescisao_gerada_w,'month')) + 86399/86400);*/
	if (ie_vencimento_fim_mes_w  = 'S') then
		dt_limite_utilizacao_w	:= last_day(dt_limite_utilizacao_w);
	end if;
	select	nextval('pls_rescisao_contrato_seq')
	into STRICT	nr_seq_rescisao_pag_w
	;

	insert into pls_rescisao_contrato(	nr_sequencia,nr_seq_pagador,dt_atualizacao,nm_usuario,
			dt_atualizacao_nrec,nm_usuario_nrec,nm_usuario_solicitacao,dt_solicitacao,
			dt_rescisao,nr_seq_motivo_rescisao,nr_seq_causa_rescisao, ie_situacao,
			ds_observacao, dt_limite_utilizacao, ie_processo, ie_tipo_solicitante)
	values (	nr_seq_rescisao_pag_w,nr_seq_pagador_p,clock_timestamp(),nm_usuario_p,
			clock_timestamp(),nm_usuario_p,nm_usuario_p,clock_timestamp(),
			dt_rescisao_gerada_w,nr_seq_mot_rescisao_w, nr_seq_causa_rescisao_w, 'A',
			wheb_mensagem_pck.get_texto(280487), dt_limite_utilizacao_w, 'M', 'T');

elsif (qt_rescisao_pagador_w > 0) then
	ds_erro_w	:= wheb_mensagem_pck.get_texto(280490);
end if;

if (ie_situacao_trab_w IS NOT NULL AND ie_situacao_trab_w::text <> '') then
	update	pls_segurado
	set	ie_situacao_trabalhista	= ie_situacao_trab_w
	where	nr_sequencia	= nr_seq_segurado_p;

	/*aaschlote - 18/01/2011 - Gerar a situação trabalhista para os dependentes também*/

	update	pls_segurado
	set	ie_situacao_trabalhista	= ie_situacao_trab_w
	where	nr_seq_titular		= nr_seq_segurado_p;

	/* Lepinski - OS 388364 */

	update	pls_contrato_pagador
	set	ie_situacao_trabalhista	= ie_situacao_trab_w
	where	nr_sequencia		= nr_seq_pagador_p;
end if;

ds_erro_p :=  ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_inserir_rescicao_pagador ( nr_seq_alteracao_motivo_p bigint, nr_seq_pagador_p bigint, nr_seq_segurado_p bigint, nr_seq_motivo_rescisao_pag_p bigint, nr_seq_causa_rescisao_p bigint, dt_alteracao_p timestamp, nm_usuario_p text, qt_contribuicao_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

