CREATE OR REPLACE PROCEDURE pls_inserir_segurado_pagador ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_segurado_w	pls_segurado.nr_sequencia%type;
dt_contratacao_w	pls_segurado.dt_contratacao%type;
nr_seq_pagador_w	pls_segurado.nr_seq_pagador%type;

type t_number_table is table of bigint index by integer;
type t_date_table is table of timestamp index by integer;
nr_seq_segurado_tw		t_number_table;
nr_seq_pagador_tw		t_number_table;
dt_contratacao_tw		t_date_table;
i_sequencia_w			integer;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_pagador,
		a.dt_contratacao
	from	pls_segurado a
	where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	(a.nr_seq_pagador IS NOT NULL AND a.nr_seq_pagador::text <> '')
	and	not exists (	SELECT	1
				from 	pls_segurado_pagador x
				where	x.nr_seq_segurado = a.nr_sequencia);


BEGIN
i_sequencia_w := 0;

open C01;
loop
fetch C01 into
	nr_seq_segurado_w,
	nr_seq_pagador_w,
	dt_contratacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	i_sequencia_w := i_sequencia_w + 1;
	nr_seq_segurado_tw(i_sequencia_w) := nr_seq_segurado_w;
	nr_seq_pagador_tw(i_sequencia_w)  := nr_seq_pagador_w;
	dt_contratacao_tw(i_sequencia_w)  := trunc(dt_contratacao_w,'month');
	end;
end loop;
close C01;

if (nr_seq_segurado_tw.count > 0) then
	forall i in nr_seq_segurado_tw.first..nr_seq_segurado_tw.last
		insert into pls_segurado_pagador(nr_sequencia, dt_atualizacao, nm_usuario,
			nr_seq_segurado, nr_seq_pagador, dt_inicio_vigencia
			)
		values (nextval('pls_segurado_pagador_seq'), clock_timestamp(), nm_usuario_p,
			 nr_seq_segurado_tw(i), nr_seq_pagador_tw(i), dt_contratacao_tw(i)
			);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
