CREATE OR REPLACE PROCEDURE pls_inserir_solic_alt_tit_web (nr_seq_contrato_p bigint, nr_seq_seg_atual_p bigint, nr_seq_seg_novo_p bigint, cd_empresa_p text, nm_usuario_p text, cd_estabelecimento_p text ) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Inserir uma nova solicitacao de transferencia de titularidade
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [X] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 			

nr_seq_solic_transf_titular_w		pls_solic_transf_titular.nr_sequencia%type;


BEGIN

begin
	select	nr_sequencia
	into STRICT	nr_seq_solic_transf_titular_w
	from	pls_solic_transf_titular
	where	nr_seq_seg_atual	= nr_seq_seg_atual_p
	and	ie_status		= '0'
	and	coalesce(dt_confirmacao::text, '') = ''
	and	coalesce(dt_rejeicao::text, '') = '';
exception
when others then
	nr_seq_solic_transf_titular_w	:= null;
end;

if (nr_seq_solic_transf_titular_w IS NOT NULL AND nr_seq_solic_transf_titular_w::text <> '') then
	update	pls_solic_transf_titular
	set	nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		nr_seq_contrato		= nr_seq_contrato_p,
		nr_seq_seg_atual	= nr_seq_seg_atual_p,
		nr_seq_seg_novo		= nr_seq_seg_novo_p
	where	nr_sequencia		= nr_seq_solic_transf_titular_w;
	
else

	insert into  pls_solic_transf_titular(	nr_sequencia,cd_estabelecimento,dt_atualizacao, 
						nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, 
						cd_empresa ,nr_seq_contrato, nr_seq_seg_atual, 
						nr_seq_seg_novo, ie_status, dt_solicitacao) 
				values (	nextval('pls_solic_transf_titular_seq'),cd_estabelecimento_p, clock_timestamp(), 
						nm_usuario_p, clock_timestamp(),nm_usuario_p,
						cd_empresa_p, nr_seq_contrato_p, nr_seq_seg_atual_p,
						nr_seq_seg_novo_p,'0',clock_timestamp());
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
