CREATE OR REPLACE PROCEDURE pls_inserir_texto_processo ( nr_seq_texto_p bigint, nr_seq_defesa_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_titulo_w			varchar(255);
ds_texto_w			text;
ie_titulo_relatorio_w		varchar(1);


BEGIN

select	ds_titulo,
	ds_texto,
	ie_titulo_relatorio
into STRICT	ds_titulo_w,
	ds_texto_w,
	ie_titulo_relatorio_w
from	pls_processo_texto_padrao
where	nr_sequencia	= nr_seq_texto_p;

insert into pls_texto_defesa(	nr_sequencia,
					nr_seq_defesa,
					ds_titulo,
					ds_defesa,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ie_titulo_relatorio)
			values (	nextval('pls_texto_defesa_seq'),
					nr_seq_defesa_p,
					ds_titulo_w,
					ds_texto_w,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					ie_titulo_relatorio_w);

commit;

end;
$body$
