CREATE OR REPLACE PROCEDURE pls_inserir_tipo_documento ( nr_seq_tipo_documento_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text, ds_arquivo_p text) AS $body$
DECLARE


nr_seq_tipo_documento_w		bigint;
qt_doc_w			bigint;
nr_seq_tipo_documento_ww	bigint;


BEGIN

select	count(*)
into STRICT	qt_doc_w
from	pls_tipo_documento_pf
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	nr_seq_tipo_documento	= nr_seq_tipo_documento_p;

if (qt_doc_w = 0) then

	select	nextval('pls_tipo_documento_pf_seq')
	into STRICT	nr_seq_tipo_documento_w
	;

	insert into  pls_tipo_documento_pf(	nr_sequencia, cd_estabelecimento, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec , nm_usuario_nrec,
			nr_seq_tipo_documento, dt_registro, nm_usuario_registro,
			cd_pessoa_fisica	)
	values (	 nr_seq_tipo_documento_w, cd_estabelecimento_p , clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			nr_seq_tipo_documento_p , clock_timestamp() , nm_usuario_p,
			cd_pessoa_fisica_p	);

	insert into  pls_documento_pf(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec ,
			nm_usuario_nrec, nr_seq_tipo_doc_pf, ds_arquivo	)
	values (	 nextval('pls_documento_pf_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp() ,
			nm_usuario_p , nr_seq_tipo_documento_w, ds_arquivo_p	);

else

	select	max(nr_sequencia)
	into STRICT	nr_seq_tipo_documento_ww
	from	pls_tipo_documento_pf
	where	nr_seq_tipo_documento	= nr_seq_tipo_documento_p
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	insert into  pls_documento_pf(	nr_sequencia,  dt_atualizacao, nm_usuario, dt_atualizacao_nrec ,
			nm_usuario_nrec, nr_seq_tipo_doc_pf, ds_arquivo )
	values (	 nextval('pls_documento_pf_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(),
			nm_usuario_p , nr_seq_tipo_documento_ww, ds_arquivo_p	);

end if;

commit;

end;
$body$
