CREATE OR REPLACE PROCEDURE pls_inserir_usuario_web ( nr_seq_comunic_p bigint, nm_usuario_web_p text, nm_usuario_p text) AS $body$
DECLARE


nm_usuario_prestador_w		pls_comunic_externa_web.nm_usuario_prestador%type;

BEGIN

select	nm_usuario_prestador
into STRICT	nm_usuario_prestador_w
from	pls_comunic_externa_web
where	nr_sequencia = nr_seq_comunic_p;

if (coalesce(nm_usuario_prestador_w::text, '') = '') then
	nm_usuario_prestador_w	:= nm_usuario_web_p;
else
	nm_usuario_prestador_w	:= nm_usuario_prestador_w || ',' || nm_usuario_web_p;
end if;

if (nm_usuario_prestador_w IS NOT NULL AND nm_usuario_prestador_w::text <> '') then
	update	pls_comunic_externa_web
	set	nm_usuario_prestador	= nm_usuario_prestador_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_comunic_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
