CREATE OR REPLACE PROCEDURE pls_inserir_valor_contas_medic ( nr_seq_aud_item_p bigint, ie_tipo_item_p text, vl_conta_medica_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Inserir no item o valor a ser cobrado nas contas médicas
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (ie_tipo_item_p	= 'P') then
	update	pls_auditoria_item
	set	vl_procedimento_conta	= vl_conta_medica_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_aud_item_p;
elsif (ie_tipo_item_p	= 'M') then
	update	pls_auditoria_item
	set	vl_material_conta	= vl_conta_medica_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_aud_item_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
