CREATE OR REPLACE PROCEDURE pls_isentar_inscricao ( nr_seq_proposta_p bigint, nr_seq_proposta_benef_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_taxa_inscricao_w	varchar(1);
nr_seq_benef_w		bigint;
nr_seq_proposta_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	pls_proposta_beneficiario	b,
		pls_proposta_adesao		a
	where	a.nr_sequencia = b.nr_seq_proposta
	and	a.nr_sequencia = nr_seq_proposta_p
	and	b.ie_taxa_inscricaO = 'S';


BEGIN

if (nr_seq_proposta_p <> 0) then
	open C01;
	loop
	fetch C01 into
		nr_seq_benef_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	pls_proposta_beneficiario
		set	ie_taxa_inscricao	= 'N'
		where	nr_seq_proposta		= nr_seq_proposta_p
		and	nr_sequencia		= nr_seq_benef_w;

		delete
		from	pls_proposta_resumo
		where	nr_seq_ordem in (6,7)
		and	nr_seq_proposta = nr_seq_proposta_p
		and	nr_seq_segurado	= nr_seq_benef_w;

		end;
	end loop;
	close C01;
elsif (nr_seq_proposta_benef_p <> 0) then
	select	a.nr_sequencia
	into STRICT	nr_seq_proposta_w
	from	pls_proposta_beneficiario	b,
		pls_proposta_adesao		a
	where	a.nr_sequencia = b.nr_seq_proposta
	and	b.nr_sequencia = nr_seq_proposta_benef_p
	and	b.ie_taxa_inscricaO = 'S';

	update	pls_proposta_beneficiario
	set	ie_taxa_inscricao	= 'N'
	where	nr_sequencia		= nr_seq_proposta_benef_p;

	delete
	from	pls_proposta_resumo
	where	nr_seq_ordem in (6,7)
	and	nr_seq_proposta = nr_seq_proposta_w
	and	nr_seq_segurado	= nr_seq_proposta_benef_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
