CREATE OR REPLACE PROCEDURE pls_isentar_taxa_inscricao ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


ie_taxa_inscricao_w		varchar(1)	:= 'S';


BEGIN

/*select	ie_taxa_inscricao
into	ie_taxa_inscricao_w
from	pls_segurado
where	nr_sequencia	= nr_sequencia_p;*/
if (ie_opcao_p	= 'A') then
	ie_taxa_inscricao_w	:= 'S';
elsif (ie_opcao_p	= 'I') then
	ie_taxa_inscricao_w	:= 'N';
end if;

update	pls_segurado
set	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp(),
	ie_taxa_inscricao	= ie_taxa_inscricao_w
where	nr_sequencia		= nr_sequencia_p;

commit;

end;
