SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_item_dentro_internacao ( nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nm_usuario_p text, ie_dentro_int_p INOUT text, ds_conta_p INOUT text) AS $body$
DECLARE


ie_internado_w			varchar(1);
dt_entrada_w			timestamp;
dt_alta_w			timestamp;
dt_proc_max_w			timestamp;
dt_proc_min_w			timestamp;
hr_inicio_w			timestamp;
hr_fim_w			timestamp;
nr_seq_segurado_w		bigint;
qt_reg_w			integer	:= 0;
ds_retorno_w			varchar(1)	:= 'N';
ds_conta_w			varchar(4000)	:= '';
nr_seq_conta_ww			bigint;
nr_seq_conta_w			bigint;
ie_tipo_guia_w			smallint;

C01 CURSOR FOR
	SELECT	distinct a.nr_sequencia
	from	pls_conta_proc		b,
		pls_conta		a
	where	a.nr_sequencia		= b.nr_seq_conta
	and	a.nr_sequencia		<> nr_seq_conta_w
	and	a.nr_seq_segurado 	= nr_seq_segurado_w
	and	(((b.dt_inicio_proc IS NOT NULL AND b.dt_inicio_proc::text <> '') and (b.dt_procedimento IS NOT NULL AND b.dt_procedimento::text <> '') and (to_date(to_char(coalesce(b.dt_procedimento,clock_timestamp()),'dd/mm/yyyy')||' '||to_char(coalesce(b.dt_inicio_proc,clock_timestamp()),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
		between dt_entrada_w and dt_alta_w))
	or	((b.dt_fim_proc IS NOT NULL AND b.dt_fim_proc::text <> '') and (b.dt_procedimento IS NOT NULL AND b.dt_procedimento::text <> '') and (to_date(to_char(coalesce(b.dt_procedimento,clock_timestamp()),'dd/mm/yyyy')||' '||to_char(coalesce(b.dt_fim_proc,clock_timestamp()),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
		between dt_entrada_w and dt_alta_w))
	or (trunc(b.dt_procedimento) between trunc(dt_entrada_w) and trunc(dt_alta_w) and (coalesce(b.dt_inicio_proc::text, '') = '' and coalesce(b.dt_fim_proc::text, '') = '')))
	and	pls_obter_se_internado(a.nr_sequencia,'') = 'N';

C02 CURSOR FOR
	SELECT	distinct a.nr_sequencia
	from	pls_conta_proc		b,
		pls_conta		a
	where	a.nr_sequencia 		= b.nr_seq_conta
	and	a.nr_sequencia 		<> nr_seq_conta_w
	and	a.nr_seq_segurado 	= nr_seq_segurado_w
	and	obter_se_datas_entre_periodo(a.dt_entrada,a.dt_alta,dt_proc_min_w,dt_proc_max_w) = 'S'
	and	pls_obter_se_internado(a.nr_sequencia,'') = 'S';


BEGIN
if (coalesce(nr_seq_conta_proc_p,0) > 0) then
	select	max(nr_seq_conta)
	into STRICT	nr_seq_conta_w
	from	pls_conta_proc
	where	nr_sequencia = nr_seq_conta_proc_p;
elsif (coalesce(nr_seq_conta_mat_p,0) > 0) then
	select	max(nr_seq_conta)
	into STRICT	nr_seq_conta_w
	from	pls_conta_mat
	where	nr_sequencia = nr_seq_conta_mat_p;
else
	nr_seq_conta_w := nr_seq_conta_p;
end if;

/*Obter se a conta que esta sendo consistida é de internação*/

ie_internado_w := pls_obter_se_internado(nr_seq_conta_w,'');

select	dt_entrada,
	dt_alta,
	nr_seq_segurado
into STRICT	dt_entrada_w,
	dt_alta_w,
	nr_seq_segurado_w
from	pls_conta
where	nr_sequencia = nr_seq_conta_w;

if (ie_internado_w = 'S') then
	/* Se for uma conta de internação, então irá verificar se existem procedimentos , em outras contas , dentro do periodo de internação e
	o mesmo beneficiario da conta consistida e que NÃO SEJAM de internação. */
	ds_conta_w := 'Contas ambulatorias do mesmo periodo:'||chr(13)||chr(10)||chr(9);

	open C01;
	loop
	fetch C01 into
		nr_seq_conta_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_conta_w := ds_conta_w || nr_seq_conta_ww|| ', ';
		qt_reg_w := qt_reg_w +1;

		if (length(ds_conta_w) > 255) then
			exit;
		end if;

		end;
	end loop;
	close C01;

	/* Se o qt_reg_w for maior que 0, então exitem procedimentos dentro do periodo de internação da conta para o mesmo beneficiario  */

	if (qt_reg_w > 0) then
		ds_retorno_w := 'S';
	end if;
else
	/* Se não for uma conta de internação, então irá verificar, com as datas dos procedimentos (dt_proc_max_w 'maoir data', dt_proc_min_w 'menor data')  da conta que
	está sendo consistida , se exitem contas de internação em que estas datas dos procedimentos estejam dentro do periodo de entrada e saida das contas de internação.
	Tambem verificando somente se for o mesmo beneficiario.*/
	ds_conta_w := 'Contas de internação do mesmo periodo:'||chr(13)||chr(10)||chr(9);

	select	max(dt_procedimento),
		min(dt_procedimento)
	into STRICT	dt_proc_max_w,
		dt_proc_min_w
	from	pls_conta_proc
	where	nr_seq_conta = nr_seq_conta_w;

	open C02;
	loop
	fetch C02 into
		nr_seq_conta_ww;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		ds_conta_w := ds_conta_w || nr_seq_conta_ww|| ', ';
		qt_reg_w := qt_reg_w +1;

		if (length(ds_conta_w) > 255) then
			exit;
		end if;

		end;
	end loop;
	close C02;
	/* Se o qt_reg_w for maior que 0, então exitem contas de internação em que o periodo de internação da conta é a mesma que as datas dos procedimentos da conta consistida */

	if (qt_reg_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;

ie_dentro_int_p	:= ds_retorno_w;

if (ds_retorno_w = 'S') then
	ds_conta_p	:= substr(ds_conta_w, 1, length(ds_conta_w) - 2);
end if;

--Não é para commit em procedures intermediarias
--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_item_dentro_internacao ( nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nm_usuario_p text, ie_dentro_int_p INOUT text, ds_conta_p INOUT text) FROM PUBLIC;

