CREATE OR REPLACE PROCEDURE pls_lag_consistir_guia_proc ( nr_seq_lote_guia_imp_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Consistir se o 'Credenciado não é habilitado a realizar o procedimento'.
	Consistir se o 'Procedimento é incompatível com o sexo do Beneficiário'.
	Consistir se a 'Idade do Beneficiário é incompatível com o Procedimento'.
	Consistir se a 'Quantidade de procedimento é maior que zero'.
	Consistir se o 'Procedimento não autorizado'.
	Consistir se o 'Procedimento ou material, inativo ou fora de vigência'.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_proc_guia_lote_imp_w	pls_lote_anexo_proc_imp.nr_sequencia%type;
nr_seq_guia_plano_w		pls_guia_plano.nr_sequencia%type;
qt_registro_w			integer;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_procedimento,
		ie_origem_proced,
		nr_seq_plano_proc
	from	pls_lote_anexo_proc_imp
	where	nr_seq_lote_guia_imp	= nr_seq_lote_guia_imp_p;

BEGIN

select	nr_seq_guia
into STRICT	nr_seq_guia_plano_w
from	pls_lote_anexo_guias_imp
where	nr_sequencia			= nr_seq_lote_guia_imp_p;

for	r_C01_w in C01 loop
	--Se não encontrar o procedimento na base gera a glosa 1801 - Procedimento inválido
	--Sequência do procedimento é obtida na importação do XML na procedure  PLS_SALVAR_LT_AX_PROC_AUT
	if ( coalesce(r_C01_w.nr_seq_plano_proc::text, '') = '' ) then
		CALL pls_inserir_anexo_glosa_aut('1801', nr_seq_lote_guia_imp_p, r_C01_w.nr_sequencia, null, 'Procedimento não encontrado na guia referência no anexo', nm_usuario_p);
	end if;

end loop;


/* Consistir a glosa '1214' - Credenciado não habilitado a realizar o procedimento*/

--pls_lag_consistir_proced_prest(nr_seq_lote_guia_imp_p, nr_seq_lote_proc_imp_w, nr_seq_guia_plano_w, nm_usuario_p);
/* Consistir a glosa 1802 - Procedimento incompatível com o sexo do Beneficiário e 1803  -  Idade do Beneficiário incompatível com o Procedimento */

--pls_lag_consistir_idade_sexo(nr_seq_lote_guia_imp_p, nr_seq_lote_proc_imp_w, nr_seq_guia_plano_w, nm_usuario_p);
/* Consistir a glosa 1806 - Quantidade de procedimento deve ser maior que zero */

--pls_lag_consistir_quant_proc(nr_seq_lote_guia_imp_p, nr_seq_lote_proc_imp_w, nr_seq_guia_plano_w, nm_usuario_p);
/* Consistir a glosa 1815 - Procedimento não autorizado */

--pls_lag_consistir_acomo_diaria(nr_seq_lote_guia_imp_p, nr_seq_lote_proc_imp_w, nr_seq_guia_plano_w, nm_usuario_p);
/* Consistir a glosa 9920 - Procedimento ou material, inativo ou fora de vigência */

--pls_lag_consistir_atv_vig_proc(nr_seq_lote_guia_imp_p, nr_seq_lote_proc_imp_w, nr_seq_guia_plano_w, nm_usuario_p);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
