SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_lancamento_sca_proposta ( nr_seq_proposta_benef_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


nr_seq_plano_w			bigint;
nr_seq_lanc_automotico_w	bigint;
nr_seq_regra_item_w		bigint;
nr_seq_sca_w			bigint;
nr_seq_tabela_w			bigint;
qt_registros_w			bigint;
nr_seq_simulacao_w		bigint;
nr_seq_tabela_perfil_w		bigint;
dt_inicio_proposta_w		timestamp;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_lanc_automatico
	where	ie_evento	= 13
	and	(((nr_seq_grupo_produto IS NOT NULL AND nr_seq_grupo_produto::text <> '') and substr(pls_se_grupo_preco_produto(nr_seq_grupo_produto,nr_seq_plano_w),1,255) = 'S') or (coalesce(nr_seq_grupo_produto::text, '') = ''))
	and	(((nr_seq_plano IS NOT NULL AND nr_seq_plano::text <> '') and	nr_seq_plano = nr_seq_plano_w) or (coalesce(nr_seq_plano::text, '') = ''))
	and	ie_situacao	= 'A'
	and	dt_inicio_proposta_w between coalesce(dt_inicio_vigencia,dt_inicio_proposta_w) and coalesce(dt_fim_vigencia,dt_inicio_proposta_w);

C02 CURSOR FOR
	SELECT	nr_seq_sca,
		nr_seq_tabela_sca
	from	pls_regra_lanc_aut_item
	where	nr_seq_regra	= nr_seq_lanc_automotico_w
	and	(nr_seq_sca IS NOT NULL AND nr_seq_sca::text <> '')
	and	ie_situacao	= 'A';


BEGIN

select	b.nr_seq_plano,
	coalesce(b.dt_contratacao,a.dt_inicio_proposta)
into STRICT	nr_seq_plano_w,
	dt_inicio_proposta_w
from	pls_proposta_beneficiario	b,
	pls_proposta_adesao		a
where	b.nr_seq_proposta	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_proposta_benef_p;

if (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_lanc_automotico_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		open C02;
		loop
		fetch C02 into
			nr_seq_sca_w,
			nr_seq_tabela_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			select	count(1)
			into STRICT	qt_registros_w
			from	pls_sca_vinculo
			where	NR_SEQ_BENEF_PROPOSTA	= nr_seq_proposta_benef_p
			and	nr_seq_plano		= nr_seq_sca_w;

			if (qt_registros_w = 0) then
				insert into pls_sca_vinculo(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
					NR_SEQ_BENEF_PROPOSTA,nr_seq_plano,nr_seq_tabela,ds_observacao,DT_INICIO_VIGENCIA)
				values (	nextval('pls_sca_vinculo_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
					nr_seq_proposta_benef_p,nr_seq_sca_w,nr_seq_tabela_w,'Gerada a partir do lançamento automático',dt_inicio_proposta_w);
			end if;
			end;
		end loop;
		close C02;
		end;
	end loop;
	close C01;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
