SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_lanc_auto_auditoria ( nr_seq_auditoria_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Lançar itens na anáçlise de autorizações conforme a regra criada 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
	Performance 
	Restritividade 
------------------------------------------------------------------------------------------------------------------- 
Referências: 
	PLS_INCLUIR_ITEM_AUDITORIA 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
cd_cgc_w			varchar(255);
ie_grupo_w			varchar(255)	:= 'S';
ie_regra_preco_w		varchar(3);
ie_restringe_estab_w		varchar(2);
ie_tipo_despesa_w		varchar(2)	:= null;
ie_tipo_item_w			varchar(1);
ie_pacote_w			varchar(1);
vl_preco_w			double precision;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_material_w		bigint;
nr_seq_regra_w			bigint;
cd_procedimento_item_w		bigint;
ie_origem_proced_item_w		bigint;
nr_seq_material_item_w		bigint;
qt_proc_regra_w			bigint;
qt_mat_regra_w			bigint;
nr_seq_prestador_w		bigint;
nr_seq_auditoria_item_w		bigint;
nr_seq_requisicao_w		bigint;
nr_seq_guia_w			bigint;
nr_seq_regra_preco_pac_w	bigint;
nr_seq_pacote_w			bigint;
nr_seq_uni_exec_w		bigint;
nr_seq_prest_fornec_w		bigint;
cd_fornecedor_w			bigint;
nr_seq_material_calc_w		bigint;
nr_seq_classificacao_w		bigint;
nr_seq_tipo_prestador_w		bigint;
nr_seq_grupo_w			bigint;
qt_item_lancamento_w		pls_regra_lanc_aut_item.qt_item_lancamento%type;
dt_preco_w			timestamp;
nr_seq_proc_w			bigint;
nr_seq_mat_w			bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.nr_seq_grupo_prestador 
	from	pls_regra_lanc_automatico	a 
	where	a.ie_situacao	= 'A' 
	and	a.ie_evento	= 10 
	/* Itens (material, procedimento) */
 
	and (a.cd_procedimento = cd_procedimento_w or coalesce(a.cd_procedimento::text, '') = '') 
	and (a.ie_origem_proced = ie_origem_proced_w or coalesce(a.ie_origem_proced::text, '') = '') 
	and (a.nr_seq_material = nr_seq_material_w or coalesce(a.nr_seq_material::text, '') = '') 
	and (a.ie_tipo_despesa = ie_tipo_despesa_w or coalesce(a.ie_tipo_despesa::text, '') = '') 
	/* Unimed */
 
	and (a.nr_seq_uni_exec = nr_seq_uni_exec_w or coalesce(a.nr_seq_uni_exec::text, '') = '') 
	/* Prestador */
 
	and (a.nr_seq_prestador = nr_seq_prestador_w or coalesce(a.nr_seq_prestador::text, '') = '') 
	and (a.nr_seq_classificacao = nr_seq_classificacao_w or coalesce(a.nr_seq_classificacao::text, '') = '') 
	and (a.nr_seq_tipo_prestador = nr_seq_tipo_prestador_w or coalesce(a.nr_seq_tipo_prestador::text, '') = '') 
	/* Geral */
 
	and	((ie_restringe_estab_w	= 'N') 
	or	(ie_restringe_estab_w	= 'S' AND a.cd_estabelecimento = cd_estabelecimento_p)) 
	and	clock_timestamp() between(coalesce(a.dt_inicio_vigencia,clock_timestamp())) and (fim_dia(coalesce(a.dt_fim_vigencia,clock_timestamp()))) 
	order by 
		coalesce(a.cd_procedimento, 0), 
		coalesce(a.nr_seq_material, 0), 
		coalesce(a.ie_tipo_despesa, '0'),-- 
		coalesce(a.nr_seq_uni_exec, 0), 
		coalesce(a.nr_seq_prestador, 0), 
		coalesce(a.nr_seq_tipo_prestador, 0), 
		coalesce(a.nr_seq_classificacao, 0), 
		coalesce(a.nr_seq_grupo_prestador, 0);

C02 CURSOR FOR 
	SELECT	a.cd_procedimento, 
		a.ie_origem_proced, 
		a.nr_seq_material, 
		a.nr_seq_prest_fornec, 
		a.qt_item_lancamento 
	from	pls_regra_lanc_aut_item	a 
	where	a.nr_seq_regra	= nr_seq_regra_w 
	and	a.ie_situacao	= 'A';


BEGIN 
begin 
select	a.cd_procedimento, 
	a.ie_origem_proced, 
	a.nr_seq_material, 
	a.ie_tipo_despesa 
into STRICT	cd_procedimento_w, 
	ie_origem_proced_w, 
	nr_seq_material_w, 
	ie_tipo_despesa_w 
from	pls_auditoria_item	a 
where	a.nr_sequencia	= nr_seq_item_p;
exception 
when others then 
	cd_procedimento_w	:= null;
	ie_origem_proced_w	:= null;
	nr_seq_material_w	:= null;
	ie_tipo_despesa_w	:= null;
end;
 
if (ie_tipo_item_p <> 'M') then 
	ie_tipo_despesa_w	:= null;
end if;
 
begin 
select	coalesce(a.nr_seq_requisicao, 0), 
	coalesce(a.nr_seq_guia, 0), 
	a.nr_seq_prestador 
into STRICT	nr_seq_requisicao_w, 
	nr_seq_guia_w, 
	nr_seq_prestador_w 
from	pls_auditoria	a 
where	a.nr_sequencia	= nr_seq_auditoria_p;
exception 
when others then 
	nr_seq_requisicao_w	:= 0;
	nr_seq_guia_w		:= 0;
	nr_seq_prestador_w	:= 0;
end;
 
select	max(a.nr_seq_classificacao), 
	max(a.nr_seq_tipo_prestador) 
into STRICT	nr_seq_classificacao_w, 
	nr_seq_tipo_prestador_w 
from	pls_prestador	a 
where	a.nr_sequencia	= nr_seq_prestador_w;
 
begin 
select	a.nr_seq_uni_exec 
into STRICT	nr_seq_uni_exec_w 
from	pls_guia_plano	a 
where	a.nr_sequencia	= nr_seq_guia_w;
exception 
when others then 
	nr_seq_uni_exec_w	:= 0;
end;
 
begin 
select	a.nr_seq_uni_exec 
into STRICT	nr_seq_uni_exec_w 
from	pls_requisicao	a 
where	a.nr_sequencia	= nr_seq_requisicao_w;
exception 
when others then 
	nr_seq_uni_exec_w	:= 0;
end;
 
ie_restringe_estab_w	:= pls_obter_se_controle_estab('LA');
 
open C01;
loop 
fetch C01 into 
	nr_seq_regra_w, 
	nr_seq_grupo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ie_grupo_w	:= 'S';
	 
	if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') and (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') then 
		ie_grupo_w	:= coalesce(substr(pls_obter_se_prestador_grupo(nr_seq_grupo_w, nr_seq_prestador_w),1,255), 'S');
	end if;
	 
	if (ie_grupo_w = 'S') then 
		open C02;
		loop 
		fetch C02 into 
			cd_procedimento_item_w, 
			ie_origem_proced_item_w, 
			nr_seq_material_item_w, 
			nr_seq_prest_fornec_w, 
			qt_item_lancamento_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
			if (cd_procedimento_item_w IS NOT NULL AND cd_procedimento_item_w::text <> '') then 
				select	count(1) 
				into STRICT	qt_proc_regra_w 
				from	pls_auditoria_item	a 
				where	a.cd_procedimento	= cd_procedimento_item_w 
				and	a.ie_origem_proced	= ie_origem_proced_item_w 
				and	a.nr_seq_auditoria	= nr_seq_auditoria_p;
 
				if (qt_proc_regra_w = 0) then 
					select 	coalesce(max(a.ie_classificacao), 1) 
					into STRICT	ie_tipo_despesa_w 
					from	procedimento	a 
					where	a.cd_procedimento	= cd_procedimento_item_w 
					and	a.ie_origem_proced	= ie_origem_proced_item_w;
 
					if (nr_seq_prestador_w	> 0) then 
						select	max(a.nr_sequencia) 
						into STRICT	nr_seq_pacote_w 
						from	pls_pacote	a 
						where	coalesce(a.nr_seq_prestador,coalesce(nr_seq_prestador_w,0)) = coalesce(nr_seq_prestador_w,0) 
						and	a.cd_procedimento	= cd_procedimento_item_w 
						and	a.ie_origem_proced	= ie_origem_proced_item_w 
						and	a.ie_situacao		= 'A';
 
						if (nr_seq_pacote_w IS NOT NULL AND nr_seq_pacote_w::text <> '') then 
							ie_tipo_despesa_w	:= '4';
						end if;
					end if;
 
					select	nextval('pls_auditoria_item_seq') 
					into STRICT	nr_seq_auditoria_item_w 
					;
 
					insert into pls_auditoria_item(nr_sequencia, 
						nr_seq_auditoria, 
						cd_procedimento, 
						ie_origem_proced, 
						qt_original, 
						dt_atualizacao, 
						nm_usuario, 
						dt_atualizacao_nrec, 
						nm_usuario_nrec, 
						qt_ajuste, 
						ie_status, 
						ie_acao_auditor, 
						ie_tipo_despesa) 
					values (nr_seq_auditoria_item_w, 
						nr_seq_auditoria_p, 
						cd_procedimento_item_w, 
						ie_origem_proced_item_w, 
						coalesce(qt_item_lancamento_w, 1), 
						clock_timestamp(), 
						nm_usuario_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						coalesce(qt_item_lancamento_w, 1), 
						'P', 
						'I', 
						ie_tipo_despesa_w);
 
					/* Francisco - 16/05/2012 - OS 447352 */
 
					if (nr_seq_pacote_w IS NOT NULL AND nr_seq_pacote_w::text <> '') then 
						select	coalesce(a.ie_regra_preco, 'N') 
						into STRICT	ie_regra_preco_w 
						from	pls_pacote a 
						where	a.nr_sequencia	= nr_seq_pacote_w;
 
						if (ie_regra_preco_w = 'S') then 
							SELECT * FROM pls_obter_regra_preco_pacote(	cd_procedimento_item_w, ie_origem_proced_item_w, 'AA', nr_seq_auditoria_item_w, nm_usuario_p, nr_seq_pacote_w, nr_seq_regra_preco_pac_w) INTO STRICT nr_seq_pacote_w, nr_seq_regra_preco_pac_w;
 
							if (coalesce(nr_seq_pacote_w::text, '') = '') and (ie_tipo_despesa_w = '4') then 
								select 	coalesce(max(a.ie_classificacao), 1) 
								into STRICT	ie_tipo_despesa_w 
								from	procedimento	a 
								where	a.cd_procedimento	= cd_procedimento_item_w 
								and	a.ie_origem_proced	= ie_origem_proced_item_w;
 
								update	pls_auditoria_item 
								set	ie_tipo_despesa	= ie_tipo_despesa_w 
								where	nr_sequencia	= nr_seq_auditoria_item_w;
							end if;
						end if;
					end if;
 
					if (coalesce(nr_seq_requisicao_w, 0) > 0) then 
						SELECT * FROM pls_consistir_itens_inseridos(	nr_seq_auditoria_p, nr_seq_auditoria_item_w, nr_seq_requisicao_w, 0, 'P', nm_usuario_p, cd_estabelecimento_p, null, nr_seq_proc_w, nr_seq_mat_w) INTO STRICT nr_seq_proc_w, nr_seq_mat_w;
					end if;
				end if;
			end if;
 
			if (nr_seq_material_item_w IS NOT NULL AND nr_seq_material_item_w::text <> '') then 
				select	count(1) 
				into STRICT	qt_mat_regra_w 
				from	pls_auditoria_item	a 
				where	a.nr_seq_material	= nr_seq_material_item_w 
				and	a.nr_seq_auditoria	= nr_seq_auditoria_p;
 
				if (qt_mat_regra_w	= 0) then 
					select 	max(a.ie_tipo_despesa) 
					into STRICT	ie_tipo_despesa_w 
					from	pls_material	a 
					where	a.nr_sequencia	= nr_seq_material_item_w;
 
					select	nextval('pls_auditoria_item_seq') 
					into STRICT	nr_seq_auditoria_item_w 
					;
 
					if (nr_seq_prest_fornec_w IS NOT NULL AND nr_seq_prest_fornec_w::text <> '') then 
						begin 
						select	a.cd_cgc 
						into STRICT	cd_cgc_w 
						from	pls_prestador	a 
						where	a.nr_sequencia	= nr_seq_prest_fornec_w;
						exception 
						when others then 
							cd_cgc_w	:= 'X';
						end;
 
						if (coalesce(cd_cgc_w, 'X') <> 'X') then 
							begin 
							select	max(a.cd_fornecedor) 
							into STRICT	cd_fornecedor_w 
							from	pls_fornec_mat_fed_sc	a 
							where	a.cd_cgc	= cd_cgc_w 
							and	a.ie_situacao	= 'A';
							exception 
							when others then 
								cd_fornecedor_w	:= 0;
							end;
 
							if (coalesce(cd_fornecedor_w, 0) <> 0) then 
								begin 
								select	max(c.dt_preco), 
									c.vl_preco 
								into STRICT	dt_preco_w, 
									vl_preco_w 
								from	pls_fornec_mat_fed_sc	d, 
									pls_mat_uni_fed_sc_preco c, 
									pls_mat_unimed_fed_sc b, 
									pls_material a 
								where	b.nr_sequencia 	= c.nr_seq_mat_unimed 
								and	b.cd_material	= a.cd_material_ops 
								and	d.cd_fornecedor	= c.cd_fornecedor_fed_sc 
								and	d.ie_situacao	= 'A' 
								and	d.cd_fornecedor	= cd_fornecedor_w 
								and	a.nr_sequencia	= nr_seq_material_item_w 
								group by 
									c.vl_preco;
								exception 
								when others then 
									vl_preco_w	:= 0;
								end;
							end if;
						end if;
					end if;
 
					insert into pls_auditoria_item(nr_sequencia, 
						nr_seq_auditoria, 
						nr_seq_material, 
						qt_original, 
						dt_atualizacao, 
						nm_usuario, 
						dt_atualizacao_nrec, 
						nm_usuario_nrec, 
						qt_ajuste, 
						ie_status, 
						ie_acao_auditor, 
						ie_tipo_despesa, 
						nr_seq_prest_fornec, 
						vl_item, 
						vl_original) 
					values (nr_seq_auditoria_item_w, 
						nr_seq_auditoria_p, 
						nr_seq_material_item_w, 
						coalesce(qt_item_lancamento_w, 1), 
						clock_timestamp(), 
						nm_usuario_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						coalesce(qt_item_lancamento_w, 1), 
						'P', 
						'I', 
						ie_tipo_despesa_w, 
						nr_seq_prest_fornec_w, 
						vl_preco_w, 
						vl_preco_w);
 
					if (coalesce(nr_seq_requisicao_w, 0) > 0) then 
						SELECT * FROM pls_consistir_itens_inseridos(	nr_seq_auditoria_p, nr_seq_auditoria_item_w, nr_seq_requisicao_w, 0, 'M', nm_usuario_p, cd_estabelecimento_p, null, nr_seq_proc_w, nr_seq_mat_w) INTO STRICT nr_seq_proc_w, nr_seq_mat_w;
					end if;
				end if;
			end if;
			end;
		end loop;
		close C02;
	end if;
	end;
end loop;
close C01;
 
--commit; 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_lanc_auto_auditoria ( nr_seq_auditoria_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

