SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_lanc_auto_item_consist ( nr_seq_conta_p pls_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Efetuar os lançamentos automáticos dos itens de conta médica na consistência da
mesma. É utilizada quando o evento a regra de lançamento for OPS - Contas medicas (item).
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_regra_w	integer;

C01 CURSOR(nr_seq_conta_pc	pls_conta.nr_sequencia%type)FOR
	SELECT	a.nr_sequencia
	from	pls_conta_proc a
	where	a.nr_seq_conta			= nr_seq_conta_pc
	and	((a.ie_status 			!= 'D') or (exists (SELECT 1
			from	pls_conta_proc	proc
			where	proc.nr_seq_proc_ref	= a.nr_sequencia
			and	proc.ie_status		!= 'D')))
	and	coalesce(a.nr_seq_participante_hi::text, '') = '';

c02 CURSOR(nr_seq_conta_pc	pls_conta.nr_sequencia%type)FOR
	SELECT	a.nr_sequencia
	from	pls_conta_mat a
	where	a.nr_seq_conta	= nr_seq_conta_pc
	and	a.ie_status 	!= 'D';
BEGIN

select	count(1)
into STRICT	qt_regra_w
from	pls_regra_lanc_automatico x
where	x.ie_evento		= 6
and	x.ie_situacao 		= 'A'
and	((x.ie_evento_lanc_conta	= 'CC') or (x.ie_evento_lanc_conta	= 'T'));

if (qt_regra_w	> 0) then
	--Cursor de procedimentos
	for r_C01_w in C01(nr_seq_conta_p) loop

		CALL pls_lanc_auto_conta_medica(nr_seq_conta_p, r_C01_w.nr_sequencia, null, nm_usuario_p, cd_estabelecimento_p, 'S');
	end loop;

	--Cursor de materiais
	for r_C02_w in C02(nr_seq_conta_p) loop

		CALL pls_lanc_auto_conta_medica(nr_seq_conta_p, null, r_C02_w.nr_sequencia, nm_usuario_p, cd_estabelecimento_p, 'S');

	end loop;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
