CREATE OR REPLACE PROCEDURE pls_lanc_auto_item_mensalidade ( nr_seq_segurado_p bigint, vl_mensalidade_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_referencia_w		timestamp;


BEGIN

dt_referencia_w	:= trunc(add_months(clock_timestamp(),1), 'month');

select	add_months(max(c.dt_mesano_referencia),1)
into STRICT	dt_referencia_w
from	pls_mensalidade_segurado	a,
	pls_mensalidade			b,
	pls_lote_mensalidade		c
where	a.nr_seq_mensalidade	= b.nr_sequencia
and	b.nr_seq_lote		= c.nr_sequencia
and	a.nr_seq_segurado	= nr_seq_segurado_p;

insert into pls_segurado_mensalidade(nr_sequencia, dt_atualizacao, nm_usuario,
	dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_segurado,
	cd_estabelecimento, dt_referencia, ie_tipo_item,
	vl_item, ie_situacao)
values (	nextval('pls_segurado_mensalidade_seq'), clock_timestamp(), nm_usuario_p,
	clock_timestamp(), nm_usuario_p, nr_seq_segurado_p,
	cd_estabelecimento_p, coalesce(dt_referencia_w,trunc(clock_timestamp(),'month')), '19',
	vl_mensalidade_p, 'A');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
