SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_lanc_bonificacao_proposta ( nr_seq_proposta_benef_p bigint, nr_seq_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE

			
nr_seq_plano_w			bigint;
nr_seq_lanc_automotico_w	bigint;
nr_seq_regra_item_w		bigint;
nr_seq_sca_w			bigint;
nr_seq_tabela_w			bigint;
qt_registros_w			bigint;
nr_seq_simulacao_w		bigint;
nr_seq_tabela_perfil_w		bigint;
dt_inicio_proposta_w		timestamp;
nr_seq_bonificacao_w		pls_regra_lanc_aut_item.nr_seq_bonificacao%type;
ie_tipo_contratacao_w		pls_plano.ie_tipo_contratacao%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_seq_bonificacao
	from	pls_regra_lanc_automatico	a,
		pls_regra_lanc_aut_item		b
	where	b.nr_seq_regra	= a.nr_sequencia
	and	a.ie_acao_regra = '7'
	and	a.ie_evento	= 2
	and	a.ie_situacao 	= 'A'
	and	b.ie_situacao	= 'A'
	and	(((a.nr_seq_grupo_produto IS NOT NULL AND a.nr_seq_grupo_produto::text <> '') and substr(pls_se_grupo_preco_produto(a.nr_seq_grupo_produto,nr_seq_plano_w),1,255) = 'S') or (coalesce(a.nr_seq_grupo_produto::text, '') = ''))
	and	(((a.nr_seq_plano IS NOT NULL AND a.nr_seq_plano::text <> '') and a.nr_seq_plano = nr_seq_plano_w) or (coalesce(a.nr_seq_plano::text, '') = ''))
	and	((a.ie_tipo_contratacao = ie_tipo_contratacao_w) or (coalesce(a.ie_tipo_contratacao::text, '') = ''))
	and	dt_inicio_proposta_w between coalesce(a.dt_inicio_vigencia,dt_inicio_proposta_w) and coalesce(a.dt_fim_vigencia,dt_inicio_proposta_w)
	and	(b.nr_seq_bonificacao IS NOT NULL AND b.nr_seq_bonificacao::text <> '');


BEGIN
select	b.nr_seq_plano,
	coalesce(b.dt_contratacao,a.dt_inicio_proposta)
into STRICT	nr_seq_plano_w,
	dt_inicio_proposta_w
from	pls_proposta_beneficiario	b,
	pls_proposta_adesao		a
where	b.nr_seq_proposta	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_proposta_benef_p;

if (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
	select	max(ie_tipo_contratacao)
	into STRICT	ie_tipo_contratacao_w
	from	pls_plano
	where	nr_sequencia = nr_seq_plano_w;

	delete	from pls_bonificacao_vinculo
	where	nr_seq_segurado_prop	= nr_seq_proposta_benef_p;
	
	open C01;
	loop
	fetch C01 into	
		nr_seq_lanc_automotico_w,
		nr_seq_bonificacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin		
		insert	into	pls_bonificacao_vinculo(	nr_sequencia,nr_seq_bonificacao,dt_atualizacao,
				nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_segurado_prop)
			values (nextval('pls_bonificacao_vinculo_seq'),nr_seq_bonificacao_w,clock_timestamp(),
				nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_proposta_benef_p);					
		end;
	end loop;
	close C01;
end if;	

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
