CREATE OR REPLACE PROCEDURE pls_lanc_bonific_simul_online ( nr_seq_simulacao_p pls_simulacao_preco.nr_sequencia%type, nr_seq_proposta_online_p pls_proposta_online.nr_sequencia%type, ie_acao_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/*
	ie_acao_p
		1 - Atualizacao da bonificacao na simulacao de preco
		2 - Copia da bonificacao da simulaco de preco para a proposta online
*/
nr_seq_plano_w		pls_plano.nr_sequencia%type;
nr_seq_simulacao_w	pls_simulacao_preco.nr_sequencia%type;
ie_tipo_contratacao_w	pls_plano.ie_tipo_contratacao%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_lanc_automatico
	where	ie_evento	= 2
	and	ie_acao_regra	= '6'
	and	(((nr_seq_grupo_produto IS NOT NULL AND nr_seq_grupo_produto::text <> '') and substr(pls_se_grupo_preco_produto(nr_seq_grupo_produto,nr_seq_plano_w),1,255) = 'S') or (coalesce(nr_seq_grupo_produto::text, '') = ''))
	and (nr_seq_plano = nr_seq_plano_w or coalesce(nr_seq_plano::text, '') = '')
	and	((ie_tipo_contratacao = ie_tipo_contratacao_w) or (coalesce(ie_tipo_contratacao::text, '') = ''))
	and	ie_situacao	= 'A';

C02 CURSOR(	nr_seq_lanc_automatico_pc	bigint) FOR
	SELECT	nr_seq_bonificacao
	from	pls_regra_lanc_aut_item
	where	nr_seq_regra	= nr_seq_lanc_automatico_pc
	and	(nr_seq_bonificacao IS NOT NULL AND nr_seq_bonificacao::text <> '')
	and	ie_situacao	= 'A'
	and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp());

BEGIN

if (ie_acao_p = '1') then
	delete	from	pls_bonificacao_vinculo
	where	nr_seq_simulacao = nr_seq_simulacao_p;
	
	select	max(nr_seq_produto)
	into STRICT	nr_seq_plano_w
	from	pls_simulacao_preco
	where	nr_sequencia	= nr_seq_simulacao_p;
	
	if (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
		select	max(ie_tipo_contratacao)
		into STRICT	ie_tipo_contratacao_w
		from	pls_plano
		where	nr_sequencia = nr_seq_plano_w;
	
		for r_c01_w in C01 loop
			begin
			
			for r_c02_w in C02(r_c01_w.nr_sequencia) loop
				begin
				insert	into	pls_bonificacao_vinculo(	nr_sequencia,nr_seq_bonificacao,dt_atualizacao,
						nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
						nr_seq_simulacao)
					values (nextval('pls_bonificacao_vinculo_seq'),r_c02_w.nr_seq_bonificacao,clock_timestamp(),
						nm_usuario_p,clock_timestamp(),nm_usuario_p,
						nr_seq_simulacao_p);
				end;
			end loop; --C02
			
			end;
		end loop; --C01
	end if;
elsif (ie_acao_p = '2') then
	select	max(nr_seq_simulacao)
	into STRICT	nr_seq_simulacao_w
	from	pls_proposta_online
	where	nr_sequencia	= nr_seq_proposta_online_p;
	
	if (nr_seq_simulacao_w IS NOT NULL AND nr_seq_simulacao_w::text <> '') then
		insert	into	pls_bonificacao_vinculo(	nr_sequencia,nr_seq_bonificacao,dt_atualizacao,
				nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_proposta_online)
			(SELECT	nextval('pls_bonificacao_vinculo_seq'),nr_seq_bonificacao,dt_atualizacao,
				nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_proposta_online_p
			from	pls_bonificacao_vinculo
			where	nr_seq_simulacao	= nr_seq_simulacao_w);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_lanc_bonific_simul_online ( nr_seq_simulacao_p pls_simulacao_preco.nr_sequencia%type, nr_seq_proposta_online_p pls_proposta_online.nr_sequencia%type, ie_acao_p text, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

