CREATE OR REPLACE PROCEDURE pls_liberarm_mens_selec ( ds_lista_mensalidades_p text, ie_opcao_p text, ie_altera_mes_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_mensalidades_w		varchar(2000);
nr_pos_virgula_w		bigint;
nr_sequencia_w		bigint;


BEGIN
if (ds_lista_mensalidades_p IS NOT NULL AND ds_lista_mensalidades_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
		ds_lista_mensalidades_w		:= ds_lista_mensalidades_p;

	while(ds_lista_mensalidades_w IS NOT NULL AND ds_lista_mensalidades_w::text <> '') loop
		begin

		nr_pos_virgula_w	:= position(',' in ds_lista_mensalidades_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_sequencia_w			:= (substr(ds_lista_mensalidades_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_mensalidades_w		:= substr(ds_lista_mensalidades_w,nr_pos_virgula_w+1,length(ds_lista_mensalidades_w));
			end;
		else
			begin
			nr_sequencia_w			:= (ds_lista_mensalidades_w)::numeric;
			ds_lista_mensalidades_w		:= null;
			end;
		end if;

		if (nr_sequencia_w > 0) then
			begin
			CALL pls_liberar_lanc_programado(
				nr_sequencia_w,
				ie_opcao_p,
				ie_altera_mes_p,
				nm_usuario_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
