CREATE OR REPLACE PROCEDURE pls_liberar_analise_conta ( nr_seq_resumo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
ie_status_w			varchar(1);
nr_seq_item_w			bigint;
ie_tipo_item_w			varchar(1);
ie_tipo_despesa_w		varchar(1);
vl_liberado_w			double precision;
				

BEGIN 
 
begin 
	select	ie_status, 
		nr_seq_item, 
		ie_tipo_item, 
		ie_tipo_despesa 
	into STRICT	ie_status_w, 
		nr_seq_item_w, 
		ie_tipo_item_w, 
		ie_tipo_despesa_w 
	from	w_pls_resumo_conta 
	where	nr_sequencia = nr_seq_resumo_p;
exception 
when others then 
	ie_status_w := null;
end;
 
if (ie_tipo_item_w = 'P') then 
	CALL pls_atualizar_conta_proc(nr_seq_item_w,nm_usuario_p);
	CALL pls_conta_aceitar_valor(null,nr_seq_item_w,null,ie_tipo_despesa_w,'M',cd_estabelecimento_p,'A',nm_usuario_p,null,null);
elsif (ie_tipo_item_w = 'M') then 
	CALL pls_atualizar_conta_mat(nr_seq_item_w,nm_usuario_p);
	CALL pls_conta_aceitar_valor(null,null,nr_seq_item_w,ie_tipo_despesa_w,'M',cd_estabelecimento_p,'A',nm_usuario_p,null,null);
end if;
 
if (ie_tipo_item_w = 'P') then 
	select	vl_liberado 
	into STRICT	vl_liberado_w	 
	from	pls_conta_proc 
	where	nr_sequencia	= nr_seq_item_w;
elsif (ie_tipo_item_w = 'M') then 
	select	vl_liberado 
	into STRICT	vl_liberado_w	 
	from	pls_conta_mat 
	where	nr_sequencia	= nr_seq_item_w;
end if;
	 
update	w_pls_resumo_conta 
set	ie_situacao	= 'L', 
	ie_status	= 'L', 
	vl_total	= vl_liberado_w, 
	qt_liberado	= qt_apresentado, 
	dt_atualizacao	= clock_timestamp(), 
	nm_usuario	= nm_usuario_p 
where	nr_sequencia	= nr_seq_resumo_p;
 
CALL pls_gerar_hist_analise(nr_seq_resumo_p, '1', nm_usuario_p, cd_estabelecimento_p);
 
commit;
 
end;
$body$
