SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liberar_grupos_analise ( nr_seq_analise_p bigint, nr_seq_regra_lib_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_analise_nova_p text) AS $body$
DECLARE


ie_status_w			varchar(255);
nr_seq_grupo_w			bigint;
nr_seq_motivo_lib_analise_w	bigint;
ds_retorno_w			varchar(255);
nr_seq_ordem_w			bigint;
qt_grupo_w			integer	:= 0;
nr_id_analise_w			bigint;
C01 CURSOR FOR
	SELECT	nr_seq_grupo,
		nr_seq_ordem
	from	pls_auditoria_conta_grupo
	where	nr_seq_analise = nr_seq_analise_p
	order by nr_seq_ordem;

BEGIN
if (ie_analise_nova_p = 'N') then

	nr_seq_motivo_lib_analise_w := obter_param_usuario(1317, 10, null, nm_usuario_p, cd_estabelecimento_p, nr_seq_motivo_lib_analise_w);

	open C01;
	loop
	fetch C01 into
		nr_seq_grupo_w,
		nr_seq_ordem_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		CALL pls_liberar_glosas_oco_usuario(	nr_seq_analise_p, nr_seq_motivo_lib_analise_w, null,
							cd_estabelecimento_p, nr_seq_grupo_w, null ,
							null,null , nm_usuario_p);
		end;
	end loop;
	close C01;
else
	open C01;
	loop
	fetch C01 into
		nr_seq_grupo_w,
		nr_seq_ordem_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_id_analise_w := pls_consulta_analise_pck.pls_gerar_w_item_analise(nr_seq_analise_p, 3, nr_seq_grupo_w, 'N', 'N', nm_usuario_p, 'N', 'N', nr_id_analise_w);
		ds_retorno_w := pls_finalizar_analise_grupo(	nr_seq_analise_p, nm_usuario_p, nr_seq_grupo_w, 'S', 'S', cd_estabelecimento_p, 'N', 'S', 'S', 'L', ds_retorno_w);
		qt_grupo_w	:= 1;
		end;
	end loop;
	close C01;
	if (qt_grupo_w	= 0) then
		CALL pls_liberar_item_pend(nr_seq_analise_p,cd_estabelecimento_p,nm_usuario_p);
	end if;
end if;

update	pls_auditoria_conta_grupo
set	dt_liberacao		= clock_timestamp(),
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_seq_analise		= nr_seq_analise_p;

update	pls_analise_conta
set	nr_seq_regra_lib	= nr_seq_regra_lib_p,
	dt_liberacao_analise	= clock_timestamp()
where	nr_sequencia		= nr_seq_analise_p;

/*atualiza as glosas da an√°lise para glosadas*/

select	max(a.ie_status)
into STRICT	ie_status_w
from	pls_analise_conta	a
where	a.nr_sequencia	= nr_seq_analise_p;

if (ie_status_w <> 'I') then
	CALL pls_alterar_status_analise_cta(nr_seq_analise_p, 'L', 'PLS_LIBERAR_GRUPOS_ANALISE', nm_usuario_p, cd_estabelecimento_p);
end if;
commit;
CALL pls_atualizar_grupo_penden(nr_seq_analise_p, cd_estabelecimento_p, nm_usuario_p);

CALL pls_fechar_conta_analise('N', nr_seq_analise_p, null, 'N' , cd_estabelecimento_p, nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
