SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_liberar_historico_solic ( nr_seq_historico_p bigint, ie_opcao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p
	C - Cliente
	S - Solicitação de lead
*/
nr_seq_cliente_w		bigint;
nr_seq_solicitacao_w		bigint;
ie_atualizar_dt_venc_w		varchar(2);


BEGIN

ie_atualizar_dt_venc_w	:= coalesce(obter_valor_param_usuario(1237, 56, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p), 'S');

if (ie_opcao_p = 'C') then
	update	pls_comercial_historico
	set	dt_liberacao	= clock_timestamp(),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_historico_p;

	select	nr_seq_cliente
	into STRICT	nr_seq_cliente_w
	from	pls_comercial_historico
	where	nr_sequencia	= nr_seq_historico_p;

	if (ie_atualizar_dt_venc_w = 'S') then /* sideker 23/07/2012 OS - 468649 */
		CALL pls_atualizar_atividades_canal(null,nr_seq_cliente_w,'N',nm_usuario_p); /* aaschlote 31/05/2011 OS - 323726 */
	end if;

	update	pls_comercial_cliente
	set	ie_classificacao	= 'P',
		ie_fase_venda		= 'PR',
		ie_status		= 'E',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_cliente_w;

	--pls_atualizar_status_cliente(nr_seq_cliente_w,nm_usuario_p);
elsif (ie_opcao_p = 'S') then
	update	pls_solicitacao_historico
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario_liberacao = nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_historico_p;

	select	nr_seq_solicitacao
	into STRICT	nr_seq_solicitacao_w
	from	pls_solicitacao_historico
	where	nr_sequencia	= nr_seq_historico_p;

	if (ie_atualizar_dt_venc_w = 'S') then /* sideker 23/07/2012 OS - 468649 */
		CALL pls_atualizar_atividades_canal(nr_seq_solicitacao_w,null,'N',nm_usuario_p); /* aaschlote 31/05/2011 OS - 323726 */
	end if;

	CALL pls_atualiza_etapa_solicitacao(nr_seq_solicitacao_w, nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
