CREATE OR REPLACE PROCEDURE pls_liberar_item_req_manual ( nr_seq_requisicao_p bigint, nr_seq_proc_p bigint, nr_seq_mat_p bigint, ds_motivo_libercao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_item_w			varchar(255);
cd_item_w			numeric(20);
ie_origem_proced_p		smallint;
ds_texto_w			varchar(4000);


BEGIN

if (nr_seq_proc_p IS NOT NULL AND nr_seq_proc_p::text <> '') then
	update	pls_requisicao_proc
	set	ie_status	= 'P',
		qt_procedimento	= qt_solicitado,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia 	= nr_seq_proc_p;

	select	cd_procedimento,
		ie_origem_proced
	into STRICT	cd_item_w,
		ie_origem_proced_p
	from	pls_requisicao_proc
	where	nr_sequencia 	= nr_seq_proc_p;
elsif (nr_seq_mat_p IS NOT NULL AND nr_seq_mat_p::text <> '') then
	update	pls_requisicao_mat
	set	ie_status	= 'P',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_mat_p;
	select	nr_seq_material,
		0
	into STRICT	cd_item_w,
		ie_origem_proced_p
	from	pls_requisicao_mat
	where	nr_sequencia 	= nr_seq_mat_p;
end if;


if (coalesce(cd_item_w,0) > 0) then
	if (coalesce(ie_origem_proced_p,0) > 0) then
		select	Obter_Descricao_Procedimento(cd_item_w,ie_origem_proced_p)
		into STRICT	ds_item_w
		;
	else
		select	pls_obter_desc_material(cd_item_w)
		into STRICT	ds_item_w
		;
	end if;
end if;

ds_texto_w := 'O usu√°rio '||nm_usuario_p||' liberou o item '||cd_item_w||' - '||ds_item_w||', pelo seguinte motivo: '||chr(13)||chr(13)||ds_motivo_libercao_p;

insert	into pls_requisicao_historico(nr_sequencia, dt_atualizacao_nrec, nm_usuario_nrec,
	dt_atualizacao, nm_usuario, ie_tipo_historico,
	ie_origem_historico, ds_historico, nr_seq_requisicao,
	dt_historico)
values (nextval('pls_requisicao_historico_seq'), clock_timestamp(), nm_usuario_p,
	clock_timestamp(), nm_usuario_p, 'M',
	'M',ds_texto_w, nr_seq_requisicao_p,
	clock_timestamp());

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
