CREATE OR REPLACE PROCEDURE pls_liberar_item ( nr_seq_item_p bigint, ie_tipo_item_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_glosa_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta_glosa
	where	nr_seq_conta_proc	= nr_seq_item_p
	and	ie_tipo_item_p		= 'P'
	and	ie_situacao = 'A'
	
union

	SELECT	nr_sequencia
	from	pls_conta_glosa
	where	nr_seq_conta_mat	= nr_seq_item_p
	and	ie_tipo_item_p		= 'M'
	and	ie_situacao = 'A';


BEGIN
if (ie_tipo_item_p = 'P') then
	update	pls_conta_proc
	set	ie_status	= 'L',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_item_p
	and	ie_status 	<> 'D';
elsif (ie_tipo_item_p = 'M') then
	update	pls_conta_mat
	set	ie_status	= 'L',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_item_p;
end if;

open C01;
loop
fetch C01 into
	nr_seq_glosa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	CALL pls_inativar_glosa_item_conta( nr_seq_glosa_w, nm_usuario_p);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
