CREATE OR REPLACE PROCEDURE pls_liberar_itens_contratante ( nr_seq_item_p bigint, ie_tipo_item_p text, ie_liberar_item_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_liberar_item_p	= 'S') then
	if (ie_tipo_item_p	= 'P') then
		update	pls_guia_plano_proc
		set	ie_status	= 'L',
			qt_autorizada	= CASE WHEN ie_status='M' THEN qt_solicitada  ELSE qt_autorizada END ,
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_item_p;
	elsif (ie_tipo_item_p	= 'M') then
		update	pls_guia_plano_mat
		set	ie_status	= 'L',
			qt_autorizada	= CASE WHEN ie_status='M' THEN qt_solicitada  ELSE qt_autorizada END ,
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_item_p;
	end if;
elsif (ie_liberar_item_p	= 'N') then
	if (ie_tipo_item_p	= 'P') then
		update	pls_guia_plano_proc
		set	ie_status	= 'K',
			qt_autorizada	= 0,
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_item_p;
	elsif (ie_tipo_item_p	= 'M') then
		update	pls_guia_plano_mat
		set	ie_status	= 'K',
			qt_autorizada	= 0,
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_item_p;
	end if;
end if;

commit;

end;
